css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com") {
body {overflow: auto !important;}
body.fbIndex { 
background: url('http://dc444.4shared.com/img/NZ5XSRF-/s7/14231164b70/t_online.jpg?async&rand=0.6707282857969403')right center fixed !important; 
-webkit-background-size: cover; 
-moz-background-size: 100% !important; 
background-repeat: no-repeat !important;
background-size: cover !important;
}

.inputtext{
    background: rgba(255,255,255,0.8);
    border-radius:3px;
    width: 150px !important;
    text-align: center !important;
}
.inputtext:focus {
    background: rgba(255,255,255,0.8);
    border-radius:3px;
    border-color: rgba(51, 166, 255, 0.6) rgba(39, 145, 225, 0.6) rgba(0, 122, 204, 0.6) !important;
}
.loggedout_menubar_container { 
  background: url('http://fiakka.com/images/blank_transparent.png') !important;
}

.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
  display: none !important;
}


.fbIndex .loggedout_menubar_container {
  position: fixed !important;
  width: 450px !important;
  height: 82px !important;
  min-width: 0 !important;
  top: 40% !important;
  left: 48% !important;
  margin-top: -17px !important;
  margin-left: -210px !important;
  z-index: -1 !important;
  text-align: center !important;
}


.fbIndex .loggedout_menubar {
  background: rgba(0,0,0,0.3) !important;
  border-radius: 7px !important;
  padding:15px 25px 25px 15px !important;
  width: auto !important;
  box-shadow: rgba(0,0,0,0.35) 5px 5px 5px; 
}

  .fbIndex .loggedout_menubar_container .lfloat,
  .fbIndex .loggedout_menubar_container .rfloat {
 float: none !important 
}

.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
 display: block !important;
 margin: 0 auto !important;
}

.fbIndex .loggedout_menubar_container .lfloat img {
 display: block; 
 margin: -60px auto 20px !important; 
}

#SetAsHomepage_Callout {
  display: none;
}
}
