@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url(chrome://browser/content/browser.xul) {

/*  Theme created by iJunkie22 -- See iJunkie22 on UserStyles.org */


    menupopup {
     -moz-appearance: none !important; 
     font-size: 9pt !important; 
     font-family: Verdana !important; 
     background: rgba(000, 000, 000, .85) !important; 
     border: 3px solid #333 !important; 
     border-radius: 4px !important 
      }
      
    menupopup * {
    color: rgba(255, 255, 255, 1) !important;
    text-shadow: none !important; 
     }
     
     #TabsToolbar {
    /*background-color: rgba(012, 012, 012, .8);*/
    }
    
	.tabbrowser-tab:hover * {
	/*background-color: rgba(255, 255, 255, .25) !important;*/
	color: rgb(045, 180, 240) !important;
	text-shadow: none !important;
	}
	/*#tabbrowser-tabs > tab:last-child {
	box-shadow:  0px -6px 0px 1000px rgba(255,255,255,.8) ;
	}*/
	
	.tabbrowser-tab[selected="true"] {
	  background: rgba(000, 000, 000, .00) !important;
	 background-color: rgba(045, 180, 240, .2) !important;
	 background-origin: padding-box !important;
	 background-clip: padding-box !important;
	 outline-style: solid !important;
	 outline-width: 4px !important;
	 /*border: 1px solid rgba(055, 180, 255, 1) !important;*/
	 border: 1px solid rgba(050, 050, 050, 1) !important;
	 border-bottom: none !important;
	 padding-left: 3px !important;
	 box-shadow: 0px 0px 1px 1px rgba(045, 140,240,1) inset,
              0px 0px 2px 2px rgba(100,100,100,.5) inset,
              -2px 2px 1px rgba(255,255,255,0) inset,
              0px -6px 0px 1000px rgba(012,012,012,.8) ;
     border-radius: 2px !important;
    border-top-right-radius: 6px !important;
    border-top-left-radius: 6px !important;
     /*-moz-image-region: rect(0px, 28px, 18px, 0px)!important;*/
     }
	.tabbrowser-tab[selected="true"]:not(:hover) * {
	   /*color: #000000 !important;*/
	   font-size: 12px !important;
	   font-weight: 400 !important;
	   text-shadow: none !important;
	   }
	.tabbrowser-tab:not([selected="true"]) {
	   background-image: none !important;
	   background: rgba(000, 000, 000, .45) !important;
	   	}
	.tabbrowser-tab:not([selected="true"]):not(:hover) * {
	    background-image: none !important;
	    color: rgba(255, 255, 255, .45) !important;
        text-shadow: 0px 0px 4px rgba(000, 000, 000, .00) !important;
	   	 }
	.tabbrowser-tab:not([selected="true"]):not(:hover) .tab-close-button * {
        display: none !important;
        }

/*.toolbarbutton-icon[class="tab-close-button"] {
    width: 24px;
}*/
	
	
.tabbrowser-tab {
	border: 50px !important;
	  border-right: 1px solid rgba(000, 000, 000, .99)  !important;
	  border-radius: 0px !important;
	  border-color: rgba(255, 255, 255, .15) !important;
	   height: 10px !important;
	   line-height: 10px !important;
	}
	
.tabs-newtab-button {
background-image: none !important;
background: transparent !important;
background-color: transparent !important;
}



.toolbarbutton-icon[label]:not([label="Back"]):not([label="Forward"]):not([type="menu"]), .toolbarbutton-icon[pinned]:not([class="tab-close-button"]) {
    margin: -6px !important;
    background-color: rgba(012, 012, 012, .9);
    /*border-top: 2px solid !important;
    border-bottom: 2px solid !important;
    border-left: 6px solid !important;
    border-right: 6px solid !important;
    border-color: rgba(012, 012, 012, .5) !important;
    border-radius: 2px !important;*/
    border: 1px solid rgba(000, 000, 000, 1) !important;
    border-left: none !important;
    border-right: none !important;
    border-radius: 1px !important;
    padding: 4px !important;
    padding-left: 6px !important;
    padding-right: 6px !important;
    height: 26px !important;
    /*fill-opacity: 0 !important;
    flood-opacity: 0 !important;*/
}

.toolbarbutton-icon[type="menu"]:not([label="Back"]):not([label="Forward"]) {
    margin: -6px !important;
    margin-left: 0px !important;
    margin-right: -18px !important;
    background-color: rgba(012, 012, 012, .9);
    /*border-top: 2px solid !important;
    border-bottom: 2px solid !important;
    border-left: 6px solid !important;
    border-right: 6px solid !important;
    border-color: rgba(012, 012, 012, .5) !important;
    border-radius: 2px !important;*/
    border: 1px solid rgba(100, 100, 100, 1) !important;
    border-radius: 1px !important;
    border-top-right-radius: 10px !important;
    /*border-top-left-radius: 5px !important;*/
    padding: 2px !important;
    padding-left: 6px !important;
    padding-right: 22px !important;
    height: 22px !important;
    /*fill-opacity: 0 !important;
    flood-opacity: 0 !important;*/
}

toolbarspring {
    background-color: rgba(012, 012, 012, .9);

}

.dropmarker-icon {
    /*margin: -6px !important;
    background-color: rgba(012, 012, 012, .9);
    padding: 2px !important;
    padding-left: 4px !important;
    padding-right: 10px !important;
    height: 22px !important;
    border: 1px solid rgba(100, 100, 100, 1) !important;
    border-left: none !important;
    text-shadow: 4px 4px 4px rgba(255, 255, 255, .95) !important;
    color: rgba(255, 012, 255, 1);*/
    /*border: 1px solid !important;*/
    /*border-color: rgba(012, 012, 012, 1) !important;*/
    /*border-radius: 2px !important;*/
    z-index: -30 !important;
    /*outline-style: solid !important;
    
    outline-color: rgba(255, 255, 255, .5) !important;
    lighting-color: rgb(0,255,255) !important;*/
}


.tab-close-button * {
padding-right: 5px !important;
}

#nav-bar:not(:-moz-lwtheme), #nav-bar[collapsed="true"] + toolbar:not(:-moz-lwtheme), #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar:not(:-moz-lwtheme), #navigator-toolbox[tabsontop="true"] > #nav-bar, #navigator-toolbox[tabsontop="true"]:not([customizing]) > #nav-bar[collapsed="true"] + toolbar, #navigator-toolbox[tabsontop="true"]:not([customizing]) > #nav-bar[collapsed="true"] + #customToolbars + #PersonalToolbar
{
	   background-image: none !important;

}

#main-window:not([disablechrome]) #navigator-toolbox {
border: 0px !important;
}
window, page, dialog, wizard, prefwindow {
-moz-appearance: none !important;
}
#navigator-toolbox > toolbar:not(#toolbar-menubar):not(#TabsToolbar)
{
padding: 0px !important;
} 

/* Compact Appmenu button */
#appmenu-toolbar-button { 
-moz-appearance: none !important;
list-style-image: url("chrome://branding/content/icon16.png") !important;
}

/* Font */
* {
  font-family: Ubuntu !important;
  font-size: 12px !important;
}


/* urlbar */
textbox {
margin: 0px !important;
}

#urlbar:-moz-lwtheme:not([focused="true"]),
.searchbar-textbox:-moz-lwtheme:not([focused="true"]) {
  /*opacity: .85;*/
  color: #ffffff !important;
  -moz-appearance: none !important;
}

#urlbar:-moz-lwtheme {
/*urlbar text*/
background: rgba(020, 020, 020, .85) !important;
border: 1px solid rgba(100, 100, 100, 1) !important;
border-radius: 3px !important;
height: 24px !important;
margin-top: -1px !important;
margin-right: -4px !important;
}


/*.urlbar-origin-label:-moz-lwtheme {
lighting-color: rgb(000,255,000) !important;
-moz-binding: url("chrome://browser/content/urlbarBindings.xml#urlbar") !important;
-moz-column-rule-color: rgb(000,255,000) !important;
-moz-text-decoration-color: rgb(000,255,000) !important;
color: rgb(000,000,255) !important;
}*/

#urlbar-progress-alt:-moz-lwtheme {
height: 20px !important;
}

}
