#hpplink a:link, #hpplink a:visited{color:silver !important;}
#hpplink a:hover{color:white !important;}
/*Links: +, Images, GMail*/
a.gb_f:link, a.gb_f:visited{color:#BDBDBD !important; transition:color 0.3s ease;}
a.gb_f:hover{color:white !important;}
/*popup google sites*/
ul.gb_u, .gb_v ,.gb_s{background-color:#333 !important;}
li.gb_a, li.gb_a *{background-color:#333 !important; color:silver !important; transition:all 0.5s ease;}
