/*Account popup*/
.gb_W{color:white !important;}
div.gb_R a:link, div.gb_Q a:visited{color:limegreen !important; transition:color 0.3s ease !important;}
div.gb_R a:hover{color:lime !important;}
a.gb_F:link, a.gb_F:visited{border:1px solid orange !important; box-shadow:0px 0xp 3px 1px orange !important; background-color:#333 !important; color:white !important; font-weight:bold !important; transition:all 0.3s ease !important;}
a.gb_F:hover{box-shadow:0px 0px 5px 2px orange !important; cursor:pointer !important;}
/*Time-Popup*/
.cdr_dlg{background-color:#333 !important;}
.cdr_ttl,.cdr_mml{color:silver !important;}
.goog-date-picker,.cdr_highl{background-color:#555 !important;}
/*Search-Results*/
#tads,#bottomads,.ads-container{display:none !important;}
h3.r > a:link, li.g > div > a:link, li.g > div > a:visited, li.g > a:link, li.g > a:visited{color:white !important;}
h3.r > a:visited{color:silver !important;}
div.s span.st{color:#BABABF !important;}
div.s div.kv * {color:limegreen !important;}
a.fl:link{color:cyan !important;}
a.fl:visited{color:cyan !important; opacity:0.8 !important;}
p a:link, p a:visited{color:orange !important; transition:color 0.2s ease !important;}
p a:hover{color:yellow !important;}
a.clickable-dropdown-arrow:link, a.clickable-dropdown-arrow:visited{background-color:transparent !important; color:lime !important;}
li.action-menu-item, li.action-menu-item a.fl:link, li.action-menu-item a.fl:visited, li.action-menu-item div.action-menu-button{background-color:#444 !important; color:white !important; font-weight:bolder !important;}
li.action-menu-item:hover, li.action-menu-item a.fl:hover,li.action-menu-item div:hover{background-color:#555 !important; color:yellow !important;}
a#irl_r_a:link, a#irl_r_a:visited{color:orangered !important; transition:color 0.2s ease !important;}
a#irl_r_a:hover{color:orange !important;}
li.ab_dropdownitem, li.ab_dropdownitem a:link, li.ab_dropdownitem a:visited{background-color:#444 !important; color:white !important; transition:all 0.3s ease !important;}
div.ab_dropdown{background-color:#444 !important;}
