a.pn:link, a.pn:visited,#newsbox span.tl>a:link{color:white !important;}
a.pn:hover{color:yellow !important;}
li.pplic div.s{color:silver !important;}
.ict{color:orange !important;}
a.qs:link, a.qs:visited,a#hdtb_more:link .mn-hd-txt, a#hdtb_more:visited .mn-hd-txt,#newsbox span.tl>a:visited{color:silver !important;}
a.qs:hover{color:white !important;}
.hdtb_mitem{border-bottom:3px solid transparent !important;}
.hdtb_mitem:hover{border-color:orange !important;}
.hdtb_msel{border-color:red !important; color:red !important;}
#hdtb_more_mn{background-color:#444 !important;}
#hdtb_more_mn .hdtb_mitem{background-color:#444 !important; color:white !important; transition:all 0.2s ease !important;}
#hdtb_more_mn .hdtb_mitem:hover, #hdtb_more_mn .hdtb_mitem:hover *{background-color:#555 !important; color:yellow !important;}
.hdtbU{background-color:#333 !important;}
.hdtb-mn-hd{color:silver !important; transition:olor 0.5s ease !important;}
.hdtb-mn-hd:hover{color:white !important;}
li.hdtbItm{background-color:#333 !important; color:white !important; transition:all 0.2s ease !important;}
li.hdtbSel{color:red !important;}
li.hdtbItm:hover, li.hdtbItm:hover *{background-color:#444 !important; color:yellow !important;}
.altcts *{color:yellow !important;}
#fll a:link, #fll a:visited{color:silver !important;}
#fll a:hover{color:white !important;}
#botstuff a:link, #botstuff a:visited,.std a:link, .std a:visited{color:orange!important; transition:color 0.2s ease !important;}
#botstuff a:hover, .std a:hover{color:orangered !important;}
div.rhsvw,div.rhsvw *{background-color:#333 !important; color:white !important;}
/*Img-Search*/
#rg_h,#cnt{background-color:#111 !important;}
#rg_hr{color:limegreen !important;}
/*Product-Overview*/
#corp-crumb a:link, #corp-crumb a:visited,.maia-aux li a:link, .maia-aux li a:visited{color:silver !important; transition:color 0.2s ease !important;}
#corp-crumb a:hover,.maia-aux li a:hover{color:white !important;}
.products-section li p{color:grey !important;}
/*Search-Input*/
.gsfi{color:white !important;}
.gstl_0,#gbqfq,#gbqfqw{background-color:#333 !important; color:white !important;}
.gssb_m, .gssb_m td,.gssb_m td *{background-color:#444 !important; color:silver !important;}
.gssb_m td:hover, .gssb_m td:hover *{background-color:#555 !important; color:white !important;}
a.gspqs_a:link, a.gspqs_a:visited, a.gssb_j:link, a.gssb_j:visited{color:orange !important;}
a.gspqs_a:hover, a.gssb_j:hover{color:yellow !important;}
.gsfe_b{border:1px solid orange !important;}
/*Search-Preview-Ads*/
#-_-gpad-_-{display:none !important;}
/*Ads*/
.ads-container-list,.ads-promo{display:none !important;}
}

