/* 2013-11-14 9:54 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("moodle.hit.ac.jp"),
regexp("(.*)(DanaInfo=moodle\\.hit\\.ac\\.jp)(.*)$") {


#page {
    margin: 0 auto !important;
    border-color: #666666 !important;
}

img[src*="logo.gif"] {
    display: none !important;
}

#header,
#header-home {
height:120px !important;
background-repeat:no-repeat;
background-image: url(data:image/png;base64,);
}

#header h1,
#header-home h1 {
    margin-top: 80px !important;
    color: #666666 !important;
}

#header div.headermenu,
#header-home div.headermenu {
    display: none !important;
}

.navbar,
.header {
    background-image: none !important;
    background: #666666;
}

.title input {
    display: none;
}

.content,
#footer {
    border-color: #666666 !important;
}

td.content {
    border-color: #dddddd !important;
}

#content {
    background: white !important;
    position: relative !important;
}

.arrow {
    color: white !important;
}

#left-column,
#right-column
 {
    background: white !important;
}

#timer {
    position: fixed !important;
    left: calc(100% - 150px) !important;
    top: 0px !important;
}

@media screen and (max-width: 980px) {
#page {
    width: calc(100% - 24px) !important;
}

#left-column {
    display: inline-block !important;
    margin: 0 -14px 0 -160px !important;
    transition: All 0.5s ease;
    float: left !important;
    position: absolute !important;
    z-index: 9999 !important;
}

#left-column:hover {
    margin: 0 -140px 0 0 !important;
    transition: All 0.5s ease;
    position: absolute !important;
    z-index: 9999 !important;
    box-shadow: 1px 1px 8px #000;
}

/*
.block_login {
    margin: 0 0 0 170px !important;
    transition: none 0s ease;
    position: absolute !important;
    z-index: 9999 !important;
}

.block_login:hover {
    position: absolute !important;
    margin: 0 0 0 0 !important;
    box-shadow: 0 0 0 #000;
    transition: none 0s ease !important;
}
*/

#middle-column {
    position: relative !important;
    z-index: 1 !important;
    padding-left: 28px !important;
    width: calc(100% - 24px - 195px) !important;
}

}

@media screen and (max-width: 640px) {
body {
    overflow-x: hidden !important;
}

#right-column {
    display: inline-block !important;
    margin: 0  -150px 0 0px !important;
    transition: All 0.5s ease;
}

#right-column:hover {
    margin: 0 0 0 0 !important;
    transition: All 0.5s ease;
    position: absolute !important;
    z-index: 9999 !important;
    box-shadow: 1px 1px 8px #000;
    right: 0px !important;
}

}

@media screen and (min-width: 1024px) {
#page {
    width: 1000px !important;
}
}

@media screen and (min-width: 1260px) {
#page {
    width: 1200px !important;
}
}

}
