css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodreads.com") {

/*Sign-In Page*/

#signedOutHome #siteheader, 
#signedOutHome .mainContentContainer{
  width: 965px !important;
  margin: 0 auto 0 auto;
}


/*Background & Font*/
body{
  background: url('http://fc08.deviantart.net/fs70/f/2011/200/d/8/airplanes_by_apofiss-d40u2t6.jpg');
  /*background: url('http://fc06.deviantart.net/fs70/f/2013/312/b/9/lee_min_ho_by_echoingdroplet-d6tg5hh.jpg');*/
  background-attachment: fixed;
  background-size: cover;
  background-position: center center;
}

body, p, ol, ul, td, th, .authorName {
  font-family: Arial !important;
  font-size: 13px !important;
}

.readable {
  font-family: Arial !important;
  font-size: 12px !important;
}

.mainContentFloat {
  font-size: 12px !important;
}

.groupPic{
  margin-top: 235px !important;
}

body a{
  color: #4747FF !important;
}

body a:hover, body a:visited {
  color: #000090 !important;
  text-decoration: none !important;
}

a.newCommentsText, a:hover.newCommentsText {
  color: #FF6666 !important;
}

.buyButtonBar a, .buyButtonBar a:hover, .buyButtonBar a:visited {
  color: #fff !important;
}

.floatingBox a {
  color: #4747FF !important;
}

.floatingBox a:hover, .floatingBox a:visited {
  color: #000090 !important;
  text-decoration: none !important;
}

em.greyText {
  color: #777 !important;
}

/*Nav Menu Properties*/
#siteheader {
  background-image: none !important;
  border-bottom: none !important;
}

.uitext, .uitext th, .uitext td, .uitext ul, .uitext ol, .uitext li {
  font-family: Segoe UI !important;
}

#siteheader > .mainContent{
  background: #181818 !important;
  border-radius: 8px 8px 0 0;
  box-shadow: 0 0 30px rgba(255,255,255,0.2);
  height: 40px !important;
  border-bottom: 5px solid #323232;
}

#logo{
  background: url('http://i884.photobucket.com/albums/ac46/the-reading-muse/Logos/goodreads-logo-grey666_zpsf0433273.png') no-repeat !important;
}

#logo > a > img {
  opacity: 0.0;
  }

#siteheader a {
  color: #666 !important;
}

#siteheader a:hover {
  text-decoration: none !important;
  color: #999 !important;
}

/*Container Background*/
.mainContentContainer > .mainContent{
  background-color: #fff;
  /*opacity: 0.9;*/
  border-radius: 0 0 8px 8px;
}

#topLanding > .mainContent {
  background-color: transparent !important;
}

/*Update Tabs*/
.mediumTabs .tab, .mediumTabs .selectedTab, .bigTabs .tab, .bigTabs .selectedTab, .h2Container {
  background-image: none !important;
}

.tabs .tab, .tabs .selectedTab {
  border-radius: 8px 8px 0 0 !important;
}

.tabs .tab {
  border: 1px solid #888 !important;
}

.tabs .selectedTab {
  border-left: 1px solid #888 !important;
  border-right: 1px solid #888 !important;
  border-top: 1px solid #888 !important;
}

.bigTabs, .mediumTabs {
  border-bottom: 1px solid #888 !important;
}

.tabs .tab {
  background: #aaa !important;
}

#nav > a.tab, a#settingsLink.tab, a#emailsLink.tab, a#feedsLink.tab, a#appsLink.tab, a#widgetsLink.tab, a#book_linksLink.tab, a#purchasesLink.tab {
  color: #fff !important;
}

#nav > a:hover.tab, a:hover#settingsLink.tab, a:hover#emailsLink.tab, a:hover#feedsLink.tab, a:hover#appsLink.tab, a:hover#widgetsLink.tab, a:hover#book_linksLink.tab, a:hover#purchasesLink.tab {
  text-decoration: none !important;
  color: #ddd !important;
}

/*Sidebar Headers*/
.h2Container, .brownBackground {
  background-image: none !important;
}

h2.brownBackground {
  color: #fff !important;
  font-family: Georgia,Times,"Times New Roman",serif !important;
}

.brownBackground {
  background-color: #666 !important;
  border-radius: 8px 8px 0 0;
}

.gradientHeaderContainer a, .gradientHeaderContainer a:visited {
  color: #fff !important;
}

.gradientHeaderContainer a:hover {
  color: #aaa !important;
  text-decoration: none !important;
}
    
/*Challenge Bar*/
.yearChallengeModule .progressBarOuter {
  border-radius: 5px !important;
}

.yearChallengeModule .progressBarOuter .progressBarInner {
  border-radius: 5px 0 0 5px !important;
}

#readingChallenge2014Header .progressBarOuter {
  border-radius: 10px !important;
}

#readingChallenge2014Header .progressBarOuter .progressBarInner {
  border-radius: 10px 0 0 10px !important;
}
      
/*Book Shelves*/
td.field.shelves {
min-width: 130px !important;
}

/*Group Banner*/
.groupMasthead {
  margin-left: -5px !important;
  width: 965px !important;
  height: 243px !important;
  background-size: cover !important;
  /*background-position: center center !important;*/
  border: 0 !important;
}

.mainContentFloat > h1 {
  margin-left: -5px !important;
  width: 812px !important;
}

/*Forum Post Headers*/
.brownBox {
  border: none !important;
  background: #ccc !important;
  border-radius: 8px 8px 0 0;
}

/*Footer*/
.footerContainer {
  background-image: none !important;
}

#readingChallenge2014Header {
  width: 965px;
  margin-left: auto;
  margin-right: auto;
}

#pagestuff.infiniteFooter:before {
  background: none !important;
}

#pagestuff.infiniteFooter .buttons {
  width: 960px !important;
  margin-left: auto;
  margin-right: auto;
}


}
