@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eventcinemas.com.au") {

  /* Remove sliders */
  .quick-times-select-widget > .cinema-row > .title,
  .quick-times-select-widget .reveal-more, 
  .quick-times-select-widget .shadow-left,
  .quick-times-select-widget .shadow-right {
    display: none !important;
  }
  .quick-times-select-widget .cinema-row-wrapper {
    overflow: visible !important;
  }
  .quick-times-select-widget .dates-row {
    width: 550px !important;
  }
  /* uncomment this to remove Gold Class buttons if you never use them */
  /*
  .quick-times-select-widget a.goldclass-btn {
    display: none !important;
  }
  */
  /* uncomment this to remove 3D session buttons if you never use them */
  /*
  .quick-times-select-widget a[data-attributes*="Digital 3D"] {
    display: none !important;
  }
  */

}
