description:
RTL version of GetPocket.com!
Font: Tahoma;
Size: 15px;
Direction: RTL;

css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("getpocket.com") {
    .grid .queue_list .item .title span {
        direction: rtl;
        text-align: right;
        font: normal 18px/1.7 Tahoma, Arial;
    }
    .queue_list .item .excerpt {
        direction: rtl;
        text-align: right;
        font: normal 11px/1.5 Tahoma;
    }
    .reader_head h1 {
        direction: rtl;
        font: bold 18px/1.7 Tahoma, Arial !important;
        text-align: right  !important;
        margin: 1em 0 0  !important;
        padding: 1.3em 0  !important;
    
    }
    .text_body p, .text_body .content {
        direction: rtl !important;
        text-align: right !important;
        font: normal 15px/1.7 Tahoma, Arial !important;
    }
.grid .queue_list .item .title span {
        direction: rtl;
        text-align: right;
        font: normal 18px/1.7 Tahoma, Arial;
    }
    .queue_list .item .excerpt {
        direction: rtl;
        text-align: right;
        font: normal 11px/1.5 Tahoma;
    }
    .reader_head h1 {
        direction: rtl;
        font: bold 18px/1.7 Tahoma, Arial !important;
        text-align: right  !important;
        margin: 1em 0 0  !important;
        padding: 1.3em 0  !important;
    
    }
    .text_body p, .text_body .content {
        direction: rtl !important;
        text-align: right !important;
        font: normal 15px/1.7 Tahoma, Arial !important;
    }
    .text_body h2, .text_body h3, .text_body h4, .text_body h5, .text_body h6 {
        direction: rtl !important;
        font-family: Tahoma, Arial !important;
        text-align: right !important;
    }
}
