@-moz-document domain("confess.ly") {
/*code by lluvia*/

/*[[dark]]*/

::-webkit-scrollbar {
  -webkit-appearance: none;
  width: 14px;
  border-radius: 8px;
}
::-webkit-scrollbar-track {
  background: rgba(248,248,248,.4);
  border-radius: 8px;
}
::-webkit-scrollbar-track:hover {
  background: rgba(248,248,248,.5);
  border-radius: 8px;
}
::-webkit-scrollbar-thumb {
  border-radius: 8px;
  /*background: rgba(255,255,255, .7);*/
  border: 2px solid rgba(35,102,118, 1); 
  /*box-shadow:   1px 1px 2px rgba(0, 0, 0, .5);*/
  background-color: #79C6C7;
  /* IE10 Consumer Preview */ 
  background-image: -ms-linear-gradient(left, #61CADF 0%, #236676 100%);
  /* Mozilla Firefox */ 
  background-image: -moz-linear-gradient(left, #61CADF 0%, #236676 100%);
  /* Opera */ 
  background-image: -o-linear-gradient(left, #61CADF 0%, #236676 100%);
  /* Webkit (Safari/Chrome 10) */ 
  background-image: -webkit-gradient(linear, left top, right top, color-stop(0, #61CADF), color-stop(1, #236676));
  /* Webkit (Chrome 11+) */ 
  background-image: -webkit-linear-gradient(left, #61CADF 0%, #236676 100%);
  /* W3C Markup, IE10 Release Preview */ 
  background-image: linear-gradient(to right, #61CADF 0%, #236676 100%);
  -ms-filter: "filter:progid:DXImageTransform.Microsoft.gradient(startColorstr='#ffffff', endColorstr='#236676', GradientType=0)";
}
    
.slimScrollBar ui-draggable {
  background-color: rgba(35,102,118, 0.7) !important; 
  width: 7px; position: absolute; 
  top: 156px; opacity: 0.9; 
  border-top-left-radius: 7px; 
  border-top-right-radius: 7px; 
  border-bottom-right-radius: 7px; 
  border-bottom-left-radius: 7px; 
  z-index: 99; right: 1px; height: 54.964197530864205px; 
  display: block; background-position: initial initial; background-repeat: initial initial;
}

.slimScrollBar ui-draggable:hover {
  background: rgba(35,102,118, 1) !important;
  border-radius: 8px;
}

.slimscrollrail {
  background: rgba(35,102,118, 0.5) !important;
}

.header a,a:active,a:focus {
  text-shadow: 0px 0px 3px rgb(0,0,0);
  -webkit-transition-duration: 0.5s, 0.3s;
  -moz-transition-duration: 0.5s, 0.3s;
  -o-transition-duration: 0.5s, 0.3s;
  -ms-transition-duration: 0.5s, 0.3s;
  transition-duration: 0.5s, 0.3s;
  -webkit-transition-timing-function: linear, ease-in;
  -moz-transition-timing-function: linear, ease-in;
  -o-transition-timing-function: linear, ease-in;
  -ms-transition-timing-function: linear, ease-in;
  transition-timing-function: linear, ease-in;
  -webkit-transition: color 0.3s ease-in;
  -moz-transition: color 0.3s ease-in;
  -o-transition: color 0.3s ease-in;
  -ms-transition: color 0.3s ease-in;
  transition: color 0.3s ease-in;
}

.sidebar a,a:active,a:focus {
  text-shadow: 0px 0px 0px rgb(0,0,0);
  -webkit-transition-duration: 0.5s, 0.3s;
  -moz-transition-duration: 0.5s, 0.3s;
  -o-transition-duration: 0.5s, 0.3s;
  -ms-transition-duration: 0.5s, 0.3s;
  transition-duration: 0.5s, 0.3s;
  -webkit-transition-timing-function: linear, ease-in;
  -moz-transition-timing-function: linear, ease-in;
  -o-transition-timing-function: linear, ease-in;
  -ms-transition-timing-function: linear, ease-in;
  transition-timing-function: linear, ease-in;
  -webkit-transition: color 0.3s ease-in;
  -moz-transition: color 0.3s ease-in;
  -o-transition: color 0.3s ease-in;
  -ms-transition: color 0.3s ease-in;
  transition: color 0.3s ease-in;
}

a:hover {
  text-shadow: 0px 0px 2px rgb(55,159,174);
  -webkit-transition-duration: 0.3s, 0.3s;
  -moz-transition-duration: 0.3s, 0.3s;
  -o-transition-duration: 0.3s, 0.3s;
  -ms-transition-duration: 0.3s, 0.3s;
  transition-duration: 0.3s, 0.3s;
  -webkit-transition-timing-function: linear, ease-in;
  -moz-transition-timing-function: linear, ease-in;
  -o-transition-timing-function: linear, ease-in;
  -ms-transition-timing-function: linear, ease-in;
  transition-timing-function: linear, ease-in;
  -webkit-transition: color 0.3s ease-in;
  -moz-transition: color 0.3s ease-in;
  -o-transition: color 0.3s ease-in;
  -ms-transition: color 0.3s ease-in;
  transition: color 0.3s ease-in;
}

.confession_form_top {
	-ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr='#61CADF',endColorstr='#3FAABE')";
	  background:-webkit-gradient(linear,left top,left bottom,from(#61CADF),to(#3FAABE));
  background:-moz-linear-gradient(top,#61CADF,#3FAABE);
  /**background: url("../img/b/exp_top1.png") no-repeat;*/
  margin-left: 10px;
  color: #ffffff;
  font-size: 16px;
  text-align: center;
  font-weight: bold;
  /*text-shadow: 1px 1px 1px rgb(0,0,0);*/
  padding-top: 15px;
  padding-left: 10px;
  max-width: 238px;
  height: 35px;
  border: 1px solid #a9a9a9;
}
  
input[type=submit]:hover {
  opacity:.90;
  box-shadow: 0px 0px 3px rgb(55,159,174);
}
   
.confession_button:hover {
  opacity:.90;
  box-shadow: 0px 0px 3px rgb(55,159,174);
}

/*[[chatroom]]*/


}

