css:
@-moz-document url("https://cat.pdx.edu/~snot/snot.cgi"), url-prefix("https://cat.pdx.edu/~snot/") {
html {
  background-color: rbga(0,0,0,0.1);
}
body {
  font-family: sans-serif;
  margin-top: 5px;
  padding: 4px;
  background-color: rgba(0, 0, 23, 0.15);
  /*background-image: -webkit-linear-gradient(top, transparent 4%, rgba(0, 0, 23, 0.2) 67%);*/
  border-radius: 6px;
}

form[action="snot.cgi"] {
  
}
form[action="snot.cgi"] b {
  display: none;
}

input[type=text] {
  padding: 2px;
  border: solid 1px gray;
  border-radius: 4px;
  outline: none;
  -webkit-transition: -webkit-box-shadow 0.15s ease;
}
input[type=text]:focus {
  -webkit-box-shadow: 0 0 5px rgba(0,0,0,0.4);
}

input[type=button],
input[type=submit],
input[type=reset],
select {
  color: #444D59;
  color: #3f444a;
  text-align: center;
  line-height: 20px;
  background-color: #E2E5EC;
  border: 1px solid #8E98A9;
  -webkit-box-shadow:inset 0px 0px 1px rgba(160,170,184,1);
  box-shadow:inset 0px 0px 1px rgba(160,170,184,1);
  background-image: -webkit-linear-gradient(bottom, rgb(214,219,223) 0%, rgb(244,244,248) 100%);
  background-image: linear-gradient(bottom, rgb(214,219,223) 0%, rgb(244,244,248) 100%);
  border-radius: 4px;
  text-shadow: rgba(255,255,255,0.5) 0px 1px 0px;
  cursor: pointer;
  outline: none;
  -webkit-transition: -webkit-box-shadow 0.15s ease;
}
input[type=button]:hover,
input[type=submit]:hover,
input[type=reset]:hover,
select:hover {
  background-color: #EAEAF0;
  background-image: -webkit-linear-gradient(bottom, rgb(229,235,240) 0%, rgb(249,248,255) 100%);
  background-image: linear-gradient(bottom, rgb(229,235,240) 0%, rgb(249,248,255) 100%);
}
input[type=button]:active,
input[type=submit]:active,
input[type=reset]:active,
select:active {
  background-color: #C6CDD3;
  background-image: -webkit-linear-gradient(bottom, rgb(244,244,248) 0%, rgb(214,219,223) 100%);
  background-image: linear-gradient(bottom, rgb(244,244,248) 0%, rgb(214,219,223) 100%);
  text-shadow: none;
}
a {
  outline: none;
  -webkit-transition: -webkit-box-shadow 0.15s ease;
}
input[type=button]:focus,
input[type=submit]:focus,
input[type=reset]:focus,
select:focus,
a:focus {
  -webkit-box-shadow: 0 0 5px rgba(0,0,0,0.4);
}

input[name="ticket"] {
  margin-left: 6px;
}

table a:focus {
  -webkit-box-shadow: 0 0 5px rgba(0,0,0,0.6);
}

select {
  padding: 3px;
}

body > hr {
  margin: 0.3em 0;
  background-color: transparent;
}

hr {
  height: 2px;
  background-color: rgba(0, 0, 0, 0.3);
  border: none;
}
hr.fat, hr.superfat {
  background-color: rgba(0, 0, 0, 0.3);
  border: none;
  border-radius: 4px;
}
table {
  border-spacing: 0;
}
table.tlist {
  box-shadow: 0 0 6px rgba(0,0,0,0.7);
}
table.tlist thead tr {
  background-color: transparent;
}
table.tlist th {
  font-size: 115%;
  text-align: center;
  padding: 3px 0 0 3px;
  background-color: transparent;
  background-image: -webkit-linear-gradient(top, rgba(255, 255, 255, 0.9) 2%, rgba(255, 255, 255, 0.6) 98%);
}
table.tlist th:not(:last-child) {
  border-right: 1px solid rgba(0,0,0,0.15);
}
table.tlist th:first-child {
  border-top-left-radius: 4px;
}
table.tlist th:last-child {
  border-top-right-radius: 4px;
}
table.tlist tr {
  padding: 2px;
  background-color: rgba(255, 255, 255, 0.6);
  border-bottom: 1px solid gray;
}
table.tlist tr:last-child td:first-child {
  border-bottom-left-radius: 4px;
}
table.tlist tr:last-child td:last-child {
  border-bottom-right-radius: 4px;
}
table.tlist tbody tr {
  
}
table.tlist tbody tr a {
  /*-webkit-transition: -webkit-transform 0.3s ease;*/
}
table.tlist tbody tr:hover a {
  -webkit-transform: scale(2);
}
table.tlist tbody tr:nth-child(even) {
  background-color: rgba(255, 255, 255, 0.8);
}
table.tlist tbody td:not(:last-child) {
  border-right: 1px solid rgba(0,0,0,0.15);
}
table.tlist tbody td:not(:nth-child(8)):not(:nth-child(9)) {
  text-align: center;
}
tr:last-child {
  border-bottom: none;
}
td {
  background-color: transparent !important;
}
}
