css:
@-moz-document url-prefix("http://www.google.com/calendar/"),url-prefix("https://www.google.com/calendar/")
{
div#onegoogbar,#vr-header
{
  display: none !important;
}

.mbox {
    position: fixed !important;
    top: 0px;
    width: 20%;
    height: 50px;
    text-align: center;
    right: 40%;
}

.wk-tzlabel, wk-dummyth {
    display: none !important;
}

.wk-weektop.wk-full-mode {
    padding-right: 0 !important;
}

#mothertable > #nav
{
  z-index:2;
  -moz-transition-duration: .2s;
  -webkit-transition-duration: .2s;
  position:fixed !important;
  width: 200px !important;
  height: 100% !important;
  padding: 30px !important;
  background-color: white !important;

  right: -280px !important;
  left: auto !important;
  border-left: #D44836 5px solid !important;
  margin-right: 20px !important;
}


#mothertable > #nav:hover {
  -webkit-transition-duration: .2s;
  -moz-transition-duration: .2s;
  position:fixed !important;
  z-index:2;
  opacity: 1 !important;
  -moz-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
          box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
          -webkit-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
  right: -30px !important;
  left: auto !important;
}

#tgnowptr {
  display: none !important;
}


div#mothertable > div#maincell > div#mainbody {
     margin: 0 !important;
}

#calcontent.eui-t div#mainbody {
  padding-top:0px;
}



.eui-t div#gridcontainer {
  background-color:rgb(0,0,0);
  margin-right:0px;
}

div.mv-event-container,.wbkt table.tg-timedevents {
  border-bottom-style:none;
  border-left-style:none;
  border-right-style:none;
  border-top-style:none;
  top:0px;
}

td.st-dtitle {
  background:none;
  color:rgb(255,255,255);
}

td.st-bg,td.st-dtitle {
  border-left-style:none;
  border-top-style:none;
}

body,div#mainnav,div#vr-nav {
  background-color:rgb(0,0,0);
  border-bottom-style:none;
}

div.date-top {
  color:rgb(255,255,255);
}

div.trans-strip {
  display:none;
}

td.st-bg.st-bg-today {
  background-color:#000;
  height:100%
}

table.st-bg-table {
  padding-bottom:10px;
}

.tg-weekend {
  background: #e8f4ff !important;
}

.wk-weektop {
  padding-left: 60px !important;
  padding-right: 13px;
}
}
