css:
@-moz-document domain("pandawill.com") {
    body {
        background-image: url("/*[[back]]*/") !important;
        background-color: #111 !important;
        background-attachment: fixed !important;
        background-clip: border-box !important;
        background-origin: padding-box !important;
        background-position: center center !important;
        /*[[repeating]]*/
    }
    .clearance-sale-home-banner103021 {
        display: none;
    }
    .product-options, .ap_tr, .ap_bl, .ap_br, #cjlunbo div, .cjf_more, .cjheader_div, select, .data-table tfoot tr, .x_ul li, .order-info-box, .data-table tbody.odd tr, .block-account .block-content, .box-account, .cjpro_gift .bx-wrapper, .info-set, .prize_right {
        background: none !important;
    }
    /* text */
    #full-specifications tbody td, .account_btn2 a, .lunbo_con_l ul li a, .gv_nav_li > ul li.menu_li a, .My_account p span, .My_account p strong, .header .head_top, .nice-select #input1, .chang_vietop ul li a, .banner_nav a, body, button, input, h1, h2, h3, h4, textarea, .cjheader_home, .cjheader_list li a, .cjheader_bot a, #effect, *[style*="color:#000"], span[style*="color:#333"], .ap_con p, .ap_item, .cjmain_happy_center ul li, .cjother ul li, .cjf_your p, .cjf_more2, .cjf_more, .cjf_hall, .cjf_free, .cjheader_search, select, .success-msg, .data-table thead th, .block .block-title strong, .error-msg, .products-grid .product-name, .x_liprice, .block-account .block-content li a, .cjf_right, .cj_pmid li, a[style="color:#222;"], .table_coupons tr td, .note-msg, .notice-msg, .display-review .left, .content_main .right p.date, .status_failcon, .helpbrowse_list li a, .help2show_list li a, .help2show_back a, .mainhelp2_browse li a {
        color: white !important;
    }
    .product-options dt label, .product-options-bottom ul li, .form-list label, .sorter, .add-to-box ul li {
        color: #ddd !important;
    }
    span[style*="color:#0000FF"] {
        color: #4479ff !important;
    }
    .cjitems h2, .cjitems_des > a, .cjother ul li {
        text-shadow: 1px 1px 1px black;
    }
    /* links */
    a, .guest-reviews .average-rating ul li, .cjf_your, .cjs a[style^="color"] {
        color: /*[[linkcolor]]*/ !important;
    }
    /* blocks */
    .cjheader_mid, .main, .ap_shadow .ap_tl, .content_main {
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.5) !important;
        border: none !important;
        box-shadow: 0 0 4px white;
        padding: 10px !important;
        border-radius: 10px !important;
        -webkit-border-radius: 10px !important;
        -moz-border-radius: 10px !important;
        -khtml-border-radius: 10px !important;
    }
    .product-essential, .nice-select ul, .My_account_show, .gv_nav_view, .gv_nav_li, .cjheader_list, .cj_div, #effect, .data-table .odd, .item.odd, .cj_more {
        background: none repeat scroll 0 0 rgba(0, 0, 0, 0.8) !important;
    }
    #full-specifications tbody tr:nth-child(2n+1), .gv_nav_li > ul li.menu_li, .cjheader_list li, #inquery_for_box th, .data-table .even, .item.even, .cjmain_happy, select:focus, .fieldset, .fieldset .legend, .addresses-list li.item, .content_title {
        background: rgba(0, 0, 0, 0.5) !important;
    }
    .cj_div {
        border-color: black !important;
    }
    .cjpro_dtop li.cjpro_dnow, .cjpro_dtop {
        border-color: white !important;
    }
    input[type="text"], textarea, .cjother ul li.cjcap, .cjf_your p, .cjf_more2, .cjf_dian, .mini-search, .cjf_also h2, input.input-text, input.input-text:focus, .cart .discount, .cart .shipping, .mainhelp2_browse li a:hover {
        background: rgba(255, 255, 255, 0.1) !important;
    }
    .success-msg {
        background-color: rgba(200, 255, 200, 0.3) !important; /* green */
    }
    .error-msg, .note-msg, .notice-msg {
        background-color: rgba(255, 200, 200, 0.3) !important; /* red */
    }
    /* headers */
    .cjheader_bot, .cjheader_t, .cjpro_dtop li.cjpro_dnow, .cart-table thead tr.first, .data-table tfoot tr.first td, .cjtrail, .order-info, .block-account .block-title, .info-set h2.legend {
        background: -moz-linear-gradient(top, #444444 0%, #222222 52%, #222222 99%) !important; /* FF3.6+ */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #444444), color-stop(52%, #222222), color-stop(99%, #222222)) !important; /* Chrome,Safari4+ */
        background: -webkit-linear-gradient(top, #444444 0%, #222222 52%, #222222 99%) !important; /* Chrome10+,Safari5.1+ */
        background: -o-linear-gradient(top, #444444 0%, #222222 52%, #222222 99%) !important; /* Opera 11.10+ */
        border-radius: 5px !important;
        -webkit-border-radius: 5px !important;
        -moz-border-radius: 5px !important;
        -khtml-border-radius: 5px !important;
        padding-left: 10px !important;
        text-shadow: none !important;
    }
    .cjtrail_div:hover {
        background: -moz-linear-gradient(top, #555555 0%, #333333 52%, #333333 99%) !important; /* FF3.6+ */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #555555), color-stop(52%, #333333), color-stop(99%, #333333)) !important; /* Chrome,Safari4+ */
        background: -webkit-linear-gradient(top, #555555 0%, #333333 52%, #333333 99%) !important; /* Chrome10+,Safari5.1+ */
        background: -o-linear-gradient(top, #555555 0%, #333333 52%, #333333 99%) !important; /* Opera 11.10+ */
    }
    .cjheader, .cjf_more, .cjpro_gift ul li, .guest-reviews .product-reviews .product-reviews-list li.left {
        border: none !important;
    }
    .data-table thead th {
        background-color: #39454e !important;
    }
    /* images */
    .slider-gallery .bx-next, .slider-gallery .bx-prev {
        background-image: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/product-view.png') !important;
    }
    .cjheader_submit {
        background-image: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/search.png') !important;
    }
    button.btn-checkout {
        background-image: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/checkout.png') !important;
    }
    img[src$="righ.png"], img[src$="right.png"] {
        background: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/scright.png') !important;
        height: 17px !important;
        padding-right: 9px !important;
        width: 0 !important;
    }
    img[src$="left.png"] {
        background: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/scleft.png') !important;
        height: 17px !important;
        padding-right: 9px !important;
        width: 0 !important;
    }
    img[src$="ClearanceSale.png"] {
        background: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/ClearanceSale.png') !important;
        height: 42px !important;
        padding-right: 243px !important;
        width: 0 !important;
    }
    img[src$="sale.png"] {
        background: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/sale.png') !important;
        height: 42px !important;
        padding-right: 243px !important;
        width: 0 !important;
    }
    img[src$="trail.png"] {
        background: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/trail.png') !important;
        height: 42px !important;
        padding-right: 243px !important;
        width: 0 !important;
    }
    img[alt="logo"] {
        background: url('http://raw.github.com/Pmmlabs/PandawillNightGlass/master/logo.png') !important;
        height: 49px !important;
        padding-right: 219px !important;
        width: 0 !important;
    }
    /* hooks */
    #cjhappy {
        left: 0 !important;
    }
    .main {
        width: 100% !important; /* column on the main page */
    }
    .btn-remove {
        display: inline-block; /* 'remove-filter' button now on right of filter name */
    }
}

@-moz-document url("https://www.facebook.com/plugins/likebox.php?href=http%3A%2F%2Fwww.facebook.com%2FPandawillFans&width=450&height=320&colorscheme=light&show_faces=true&border_color&stream=true&header=true") {
    .uiStreamRedesign .uiStreamMessage .messageBody {
        color: white !important;
    }
    .uiBoxLightblue {
        background: rgba(255, 255, 255, 0.1) !important;
    }
    .uiHeader h3, .uiHeader h4 {
        color: white !important;
    }
}
