css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fstoppers.com") {
    /* common links */
    a { text-decoration:none; }
    a:link { text-decoration:none; }
    a:hover { text-decoration:underline; }
    a.top-scroll:hover {background-color:hsla(205.714, 11%, 38%, 0.8); }
    
    /* header */
    .header-links li { padding:0 !important; }
    .header-links li:after { content:" |"; color:hsl(0, 0%, 85%); }
    .header-links li:last-child:after { content:""; }
    .social { display:none; }
    
    /* breadcrumbs */
    .breadcrumb .crumb { padding-left:15px; padding-right:7px; }
    
    
    /* content */
    .node .meta .read-more a { background-color:hsl(205.714, 11%, 38%); }
    .node .meta .read-more a:hover { background-color:hsl(206.349, 81%, 46%); }
    /* .node .meta .read-more a { background-color:hsl(206.349, 81%, 46%); } */
    /* .node .meta .read-more a:hover { background-color:hsl(206.203, 89%, 35%); } */
    
    .node .meta .article-comments a:hover
   ,.full header .submitted .comment-count a:hover
    { text-decoration:underline; }
    
    /* right column */
    #block-fs_helpers-ad_Sidebar_4 { display:none; }
    #scrollgroup-2 > div:nth-child(1) { display:none; }
    #scrollgroup-5 { display:none; }
    
}
