css:
@media print {
        h2, h3, h4, h5 { page-break-after: avoid; }
        p, h2, h3, h4, h5 { orphans: 3; widows: 3; }
        pre, blockquote { page-break-inside: avoid; }
        thead { display: table-header-group; } 
        table, 
  		tr, 
  		img, 
  		figure,
  		.post-review-author { page-break-inside: avoid; }

		/*a[href]:after { 
			content: " (" attr(href) ")"; 
		}*/

		a[href^="#"]:after,
		a[href^="http://lifehacker"]:after,
  		.wp-thumbnail-caption  a[href]:after,
  		a[href^="javascript:void(0)"]:after { 
			content: "" !important; 
        }
                         
        #header,
        #popup,
        .cats-subheader,
        #scrollUp,
        .post-subborder,
        #content .post h1 > span,
        #sidebar,
        .post-top,
        .post-info,
        #lh-share,
  		#essb_displayed_both,
        #footer,
  		#masthead,
        .embed-youtube,
		.anpn-wrap-style1,
		.social-likes__wrapper,
		.sb-similar__title,
		.sb-similar__top-container,
		.readmore,
		#upprev_box,
  		iframe,
  	 	.entry-widgets.cf,
		#nav-single,
  		footer.entry-meta,
  		#waq1
        { display: none !important; }
        
        #content,
        #page,
        #comments {
            width: 98% !important;
        }
        
        #content {
        	margin-top: 0 !important;
        	border: none !important;
        }
        
        #content .post-image {
        	margin: 0 !important;
        	position: static !important;
        }
        
        #content .post {
            padding: 0 !important;
        }
        
        #content .post .post-content {
            margin-left: 0 !important;
        }
        
        #content .post .post-naked {
            margin-top: 0 !important;
    		max-width:100%;
        } 
  
        #content .post-naked p {
            text-align: justify;
        }
        
        #content .post-naked p,
        #content .post-naked li,
        #content .post-naked td,
        #content .post-naked th,
        .comment-body {
            font-size: 150% !important;
        }
        
        #content .post-naked td,
        #content .post-naked th {
            border-bottom: solid 1pt #000000 !important;
        }
        
        #content .post-naked h2 {
            border-bottom: 2pt solid #000000 !important;
        }
        
        #page {
            margin: 0 !important;
            padding: 0 1em !important;
            width: 98% !important;
        }


        .comments-title-comment,
        .comment-bottom,
        .comment-head-badges,
        #respond {
            display: none !important;
        }


		/* uncomment to hide comments */
        /*#comments,
		#hypercomments_widget {
            display: none !important;
        }*/

		/* hypercomments */
        .hc_top_block,
		.hc_menu_box,
		#hc_main_form,
		.hc_footer_link,
        .hc_photo,
		.hc_comments_time,
  		.hc_footer {
  			display: none !important;
		}
      
      	.hc .hc_message .hc_message {
          margin-left: 40px;
          padding-bottom: 0;
          margin-top: 5px;
        }
        
        .hc .hc_message {
          padding-top: 18px;
          padding-bottom: 18px;
          border-top: 1px solid #ededed!important;
        }
            
        .hc .hc_message {
			border-top: 1px dotted #ccc !important;
        }

        .hc_message_text {
        	font-size: 120%;
        }

    }
