css:
@-moz-document domain(inmedia.revues.org) {
	/* your code goes here */

* {border:none;background:transparent}
/* Éléments à ne pas imprimer */
#openbarre, #partenaires, #access, #toolbox, #nav, #news, #footer, #widgets, #shortcuts, #navAbstracts, #toc, #alphabet,
hr, #cleo, #breadcrumb, #lodel-desk, #siteContext, #warning, #publiInformation a,
div.navEntities, a.go-top, a.go-back, a.fullSize, ul.sidenotes, #logos, .textIconAccess {display:none}

body {margin:0; padding:0; font:10pt/1.4 Georgia, serif; background:#FFF; color:#222;}
a {color:#231F20;text-decoration:none;}
sup {vertical-align:text-top;}
h1 {text-align:left; font:bold 20pt Arial, sans-serif; margin:25pt 0 10pt 0;}
h2 {text-align:left; font:bold 16pt Arial, sans-serif; margin:25pt 0 10pt 0;}
h3 {text-align:left; font:italic normal 14pt Arial, sans-serif; margin:25pt 0 10pt 0;}
h4 {text-align:left; font:normal 12pt Arial, sans-serif; margin:0;}
h5 {text-align:left; font:bold 9pt Arial, sans-serif; margin:0;}
h6 {text-align:left; font:normal 9pt Arial, sans-serif; margin:0;}
#header, #wrapper {margin:0 20% 0 8%;}
h1#siteName {font:normal 32pt/1.2 Georgia, serif; margin:0;}
h2.section {margin-top:20pt; padding-top:10pt; border-top:.5pt solid #CCC; font:italic bold 12pt/1.2 Georgia, serif;}
#errata h2.section, #dedication h2.section, #ndlr h2.section, #ndla h2.section {border:none; margin:0; font:normal bold 9pt/1.3 Arial, sans-serif;}
#entries div, #errata p, #dedication p, #ndlr p, #ndla p {font:9pt/1.4 Georgia, serif; margin:0;}
#publiHeader, #docHeader {font:normal 9pt/1.4 Arial, sans-serif; margin:10pt 0 0 0; padding:10pt 0 20pt 0; border-bottom:10pt solid #DDD; width:116%;}
#publiTitle, #docTitle {font-size:28pt; line-height:1.1;}
#publiSubtitle, #docSubtitle {font-size:16pt; margin:3pt 0 5pt 0;}
/* sommaire */
ul.summary {padding:0; list-style:none;}
ul.summary li {margin:10pt 0 0 0;}
ul.summary div.texte li {margin:0 0 0 20pt; list-style:disc; text-align:left;}
/*ul.summary h2.section { margin-bottom:0 }*/
ul.summary .subtitle {font:10pt Georgia, serif; margin:0;}
ul.summary .altertitle {font:italic 10pt Georgia, serif; margin:0;}
ul.summary .author {font:bold 9pt Arial, sans-serif;}
ul.summary .title, dd.title {font-size:12pt;}
ul.summary li.textes .subtitle, ul.summary li.fichiers .subtitle, dd.subtitle {font:10pt Arial, sans-serif;}
/* article */
#docTopParent a {font:12pt Georgia, serif; color:#FFF;}
#docTopParent a span {color:#231F20;}
#docTopParent a span.title {display:block;}
#docSubParent {margin:2pt 0 0;}
#docTitle {border-top:.5pt solid #CCC; padding:10pt 0 0; margin:40pt 0 0;}
#docAuthor {margin-top:15pt; font:15pt Georgia, serif; font-variant:small-caps;}
#docAuthor strong, #docTranslator strong {font-weight:normal;}
#publiAltertitle, #docAltertitle, #docPagination, #docReference {margin:10pt 0 0;}
#publiAltertitle div, #docAltertitle div {margin-top:5pt; font-style:italic;}
#docReference p {margin:0;}
#entries div.index h3 {font:normal bold 9pt/1.4 Arial, sans-serif; display:inline; margin-right:4pt;}
#toc div.tocSection1 {font-weight:bold;}
#toc div.tocSection2 {padding-left:10pt;}
#toc div.tocSection3 {padding-left:20pt;}
#toc div.tocSection4 {padding-left:30pt; font-size:9pt;}
#toc div.tocSection5 {padding-left:40pt; font-size:9pt;}
#toc div.tocSection6 {padding-left:50pt; font-size:9pt;}
#text {text-align:justify;}
#abstract, #bibliography, #annexe, #notes {font:9pt/1.2 Georgia, serif; text-align:justify;}
#quotation, #authors, #license {font:9pt/1.2 Arial, sans-serif;}
#bibliography p, #annexe p, #notes p {margin:0 0 5pt 0;}
#abstract h3 {font:normal bold 9pt/1.4 Arial, sans-serif; margin:10pt 0 0;}
#abstract p {margin:0 0 10pt 0; text-align:justify;}
#quotation h3 {margin-bottom:0; font:italic normal 8pt Georgia, serif; margin:0;}
#quotation p {margin:0 0 10pt 0;}
#quotation strong {font-weight:normal;}
#authors h3 {font:normal bold 9pt Arial, sans-serif; margin:0;}
#authors h4 {font:italic normal 8pt Georgia, serif; margin:5pt 0 0 0;}
#authors ul.documents {margin:0 0 0 10pt; padding:0; list-style:none;}
#authors ul.documents div.title {font-weight:bold; margin-top:5pt;}
#authors p.description {margin:0 0 9pt 0;}
/* tableaux */
table {border-collapse:collapse;}
td {font:9pt Arial, sans-serif; padding:2pt; border:.5pt solid #CCC;}
td p.texte, div.textIcon p.texte {margin:0;}
/* notes */
a.footnotecall, a.endnotecall {font:normal 7pt/1 Georgia, serif; vertical-align:text-top;}
#docTitle a.footnotecall, #docTitle a.endnotecall {font-size:50%;}
#docSubtitle a.footnotecall, #docSubtitle a.endnotecall {font-size:70%;}
div.in, span.fullText {font:normal normal 8pt/1.5 Arial, sans-serif;}
/* styles internes */
p.separateur, p.sparateur {text-align:center;}
p.dedicace {}
p.remerciements {}
p.epigraphe, p.pigraphe {text-align:right;}
p.titreillustration {font:bold 9pt Arial, sans-serif; text-align:left; margin:10pt 0 5pt 0;}
p.legendeillustration, p.crditillustration, p.crditsillustration {font:normal 8pt/1.3 Arial, sans-serif; text-align:left; margin:5pt 0;}
p.crditillustration, p.crditsillustration , p.creditillustration {font:normal 8pt/1.3 Arial, sans-serif; text-align:left; margin:5pt 0; color:#666;}
p.question {font:normal 9pt/1.3 Arial, sans-serif;}
p.texte, p.annexe, p.reponse, p.bibliographie {position:relative; margin:0;}
p.texte {text-indent:10pt;}
td p.texte, div.textIcon p.texte {margin:.4em .2em; text-indent:0; text-align:left; line-height:1.3;}
span.paranumber {display:block; position:absolute; top:0; left:-35pt; width:20pt; text-align:right; font:7pt/2 Arial, sans-serif;}
p.paragraphesansretrait {text-indent:0; position:relative; margin:0;}
p.citation {font:normal 9pt/1.3 Georgia, serif; text-align:left; color:#666;}
p.citationbis {font:normal 9pt/1.3 Arial, sans-serif; text-align:left; color:#666;}
p.citationter {font:italic 9pt/1.3 Arial, sans-serif; text-align:left; color:#999;}
p.puces {text-align:left; margin:0;}
}
