css:
@-moz-document domain(boards.4chan.org) {

/* dont use "padding" for reply to OP */
.postContainer.opContainer:after { display: block; content: ""; clear: both; }
body {
	background: #282321 url(http://i3.minus.com/iCwVZyCiTfkZe.png) repeat !important;
	color: #a09888 !important;
	font-size: 16px !important;
}
div#boardNavDesktop {
	color: #a09888 !important;
}
.postInfo, .fileInfo {
	font-size: 13px !important;
}
.name {
	font-size: 17px !Important;
}
.globalMessage {
	color: #c8bcb8 !important;
	text-align: left !important;
}
a,
.button,
.quoteLink, .quotelink,
.postertrip {
	color: #e1cf8f !important;
	text-decoration: none !important;
}
.deadlink {
	color: #666055 !important;
}
a:hover {
	color: #f4e9c2 !important;
}
.name,
.subject,
.postNum a {
	color: #a09888 !important;
}
.sideArrows, body > hr {
	display: none !important;
}
.reply {
	background: transparent !important;
	border: none !important;
}
.thread {
	margin: 25px !important;
}
#quote-preview {
	box-shadow: 0px 1px 2px #121210;
}
#threads .thread,
.extPanel.reply,
#quote-preview,
.postContainer.replyContainer,
div.pagelist,
.globalMessage,
table.postForm > tbody > tr > td:first-child {
	border: none !important;
	background: #403930 !important;
	border-left: 20px solid #181714 !important;
	padding: 25px 10px !important;
	margin: 25px !important;
}
.extPanel.reply {
	height: 90% !important;
}
table.postForm > tbody > tr > td:first-child {
	padding: 5px 10px !important;
	color: #A09888 !important;
}
.opContainer .fileThumb img { 
	box-shadow: 0px 0px 0px 1px #000, 0px 0px 0px 2px #fff, 0px 0px 5px 5px #000;
}
hr {
	height: 45px !important;
	background: transparent !important;
	border: none !important;
}

input[type="text"],
input[type="checkbox"],
input[type="password"],
input[type="file"],
textarea,
*[name="com"][cols="48"] /* opera bug fix */ {
	background: #0e0e0e !important;
	border: 1px solid #342d2a !important;
	box-shadow: 0px 0px 4px rgba(0,0,0,0.5) !important;
	color: #5f5551 !important;
	padding: 9px 9px !important;
}
input[type="checkbox"] { box-shadow: none !important; }

input[type="checkbox"]:checked,
input[type="text"]:focus,
input[type="password"]:focus,
input[type="file"]:focus,
textarea:focus,
*[name="com"][cols="48"]:focus {
	background: #191716 !important;
	border-color: #4c4542 !important;
	color: #bca69d !important;
}
.button,
button,
.cataloglink a,
input[type="button"],
input[type="submit"] {
	border: 1px solid #342d2a !important;
	background: linear-gradient(to bottom, #131313, #202020) !important;
	box-shadow: 0px 0px 4px rgba(0,0,0,0.5) !important;
	color: #bca69d !important;
	border-radius: 3px !important;
	padding: 9px 20px !important;
	cursor: pointer;
}
.button:hover,
button:hover,
.cataloglink a:hover,
input[type="button"]:hover,
input[type="submit"]:hover {
	border-color: #3c3633 !important;
	color: #d3c3bd !important;
	
}
.btn-wrap::after, .btn-wrap::before {
	content: "" !important;
}
#captchaContainer {
	width: auto !important;
}
#recaptcha_area *,
div.pagelist div.cataloglink {
	border: none !important;
}
div.pagelist .pages {
	margin-right: 20px !important;
}
div.pagelist > div { 
	display: inline-block !important;
	vertical-align: middle !important;
	float: none !important;
}

/* submit topic area */
body > form[name="post"]:before {
	content: "Move cursor over here for a cake";
}
div#doc > div#bd > div.yui-g:before {
	display: block !important;
	text-align: center !important;
	width: 100% !important;
	border: 2px dashed #403930 !important;
	display: block !important;
	text-align: center !important;
	margin: 15px auto 15px auto !important;
	padding: 13px !important;
	background: transparent !important;
}
div#doc > div#bd > div.yui-g > div {
	display: none !important;
}
div#doc > div#bd > div.yui-g:hover > div {
	display: block !important;
}
body > form[name="post"] {
	border: 2px dashed #403930 !important;
	display: block !important;
	text-align: center !important;
	margin: 10px 20px !important;
	padding: 13px !important;
	background: transparent !important;
}
form table#postForm {
	display: none !important;
	background: transparent !important;
	padding: 12px !important;
	border-radius: 4px !important;
}
body > form[name="post"]:hover table#postForm {
	display: table !important;
	text-align: left !important;
}

} /* @-moz-document */
