css:
@-moz-document domain("youtube.com") {
/* WiderTube - YouTube 720p */

.player-api {
	width: 1280px !important;
	height: 720px !important;
    position: absolute !important;
    background: transparent !important;
}
  
#player, #placeholder-player {
	margin-top: 0 !important;
}

.watch-stage-mode .player-height {
	height: 720px !important;
}

#player, .cardified-page #player.watch-large, .cardified-page #player.watch-medium , .cardified-page #player.watch-small {
	background-color: #1B1B1B !important;
	transition: none !important;
	-moz-transition: none !important;
	-o-transition: none !important;
	-webkit-transition: none !important;
}

.site-center-aligned #player, .site-center-aligned #player.watch-small, .site-center-aligned #player.watch-medium {
	width: 100% !important;
	margin-bottom: 0 !important;
	background-color: #1B1B1B !important;
}
  
#player.watch-small {
	min-width: 100% !important;
}

.cardified-page #player {
	width: auto !important;
	margin: 0 auto !important;
}

#player .player-api {
	position: relative !important;
	left: 0 !important;
}

.cardified-page #player #player-api {
	float: inherit !important;
	margin: 0 auto !important;
}

.watch-stage-mode .player-width,
.site-center-aligned #alerts, .site-center-aligned #content {
	width: 1280px !important;
}

.site-center-aligned .watch #content.content-alignment {
	min-width: 1280px !important;
	max-width: 1280px !important;
}

#watch7-main-container {
	width: 1279px !important;
	border: 1px solid #e6e6e6 !important;
	background-color: #fff !important;
	border-top-width: 0 !important;
	margin: 0px auto !important;
	box-shadow: none !important;
}

.watch-wide > #watch7-main-container {
	margin: 4px auto !important;
}

#watch7-content {
	width: 960px !important;
	border-right: 1px solid #e6e6e6 !important;
}

#watch-discussion {
	max-width: 979px !important;
}

#watch7-main-container >* .yt-card {
	box-shadow: none !important;
}

.watch-sidebar {
	max-width: 300px !important;
}
  
#watch7-sidebar {
	margin-left: 979px !important;
	/*border-left: 1px solid #e6e6e6 !important;*/
}

.watch-non-stage-mode #watch7-sidebar {
    top: 390px !important;
}

.cardified-page .yt-card {
	box-shadow: 0 0 2px rgba(0,0,0,.1) !important;
}

/* html5 video player */
.html5-main-video, .html5-video-content, .html5-video-player .video-click-tracking, .html5-video-player .video-stream {
	width: 1280px !important;
	height: 720px !important;
	left: 0 !important;
	top: 0 !important;
}

iframe.youtube-player >* .html5-main-video, .html5-video-content, .html5-video-player .video-click-tracking, .html5-video-player .video-stream,
#player1 .html5-main-video, .html5-video-content, .html5-video-player .video-click-tracking, .html5-video-player .video-stream {
	/* fixes html5 videos embedded on other sites in an iframe */
	width: 100% !important;
	height: 100% !important;
	left: 0 !important;
	top: 0 !important;
}

.html5-video-container {
	bottom: 0 !important;
}

/* hiding html5 video controls */
.html5-video-player .html5-video-controls {
	visibility: hidden !important;
	transition: all 0.1s ease-out; 
	-webkit-transition: all 0.1s ease-out;
	-moz-transition: all 0.1s ease-out;
	opacity: 0 !important;
	height: 0 !important;
}

.html5-video-player.paused-mode .html5-video-controls,
.html5-video-player.ended-mode .html5-video-controls {
	visibility: visible !important;
	opacity: 1 !important;
	height: 35px !important;
}

.html5-video-player:hover .html5-video-controls {
	visibility: visible !important;
	opacity: 1 !important;
	height: 35px !important;
}

.html5-video-player >* .annotation.annotation-type-custom /* the little thingie with the branding icon */ {
	visibility: hidden !important;
	transition: all 0.1s ease-out; 
	-webkit-transition: all 0.1s ease-out;
	-moz-transition: all 0.1s ease-out;
	opacity: 0 !important;
}

.html5-video-player:hover >* .annotation.annotation-type-custom {
	visibility: visible !important;
	opacity: 1 !important;
}

/*.html5-progress-bar {
	width: 100% !important;
}

.ytp-scrubber-pull-indicator::after {
	min-width: 1280px !important;
}*/

.annotation.annotation-type-custom {
	left: 860px !important;
}

/* Feed container */
.cardified-page .feed-item-main-content,
.feed-item-container .feed-item-main-content {
	max-width: 1280px !important;
}

.feed-item-main-content >* .compact-shelf,
.feed-item-main-content >* .shelf-wrapper {
	min-width: 1000px !important;
}

.branded-page-v2-masthead-ad-header.masthead-ad-expanded .branded-page-v2-primary-col {
	border-top-width: 0 !important;
}

/* Comments width (Needs apis.google.com domain) */
#comments-test-iframe, #comments-test-iframe iframe, #widget_bounds {
	min-width: 920px !important;
	width: 920px !important;
}

/* Prevents the guide menu from pushing the page */
.exp-appbar-open-guide.guide-pinning-enabled.show-guide-if-wide #page, .exp-appbar-open-guide.guide-pinning-enabled.show-guide #page,
.show-guide-if-wide.guide-pinned.guide-enabled #page, .show-guide.guide-pinned.guide-enabled #page {
	padding-left: 0 !important;
}
      
.show-guide #appbar-guide-menu {
	box-shadow: 1px 0 2px 1px rgba(0,0,0,.05) !important;
}

/* Ads */
.ad-div, .masthead-ad-control, #ad_creative_1, #ad_creative_2, #ad_creative_3, #footer-ads, .video-ads, .ad-container {display:none !important;}

/* SITE LEFT ALIGNED START */
.site-left-aligned.guide-enabled #player, .site-left-aligned.guide-enabled #watch7-main-container {
	padding-left: 0 !important;
	margin: 0 auto !important;
}

.site-left-aligned #player.watch-playlist #player-api, .cardified-page #player #player-api, #player #player-api {
	margin: 0 auto !important;
    float: none !important;
}

.site-left-aligned .watch7-playlist-bar-secondary-controls,
.site-left-aligned #watch7-playlist-bar-controls,
.site-left-aligned .watch7-playlist-bar-left,
.site-left-aligned .watch7-playlist-bar {
	background-color: #2a2a2a !important;
}

.site-left-aligned .watch7-playlist-bar-right {
	margin-left: 1280px !important;
}

.site-left-aligned .branded-page-v2-primary-col {
	min-width: 1100px !important;
}

.site-left-aligned .cardified-page .feed-item-main-content,
.site-left-aligned .feed-item-container .feed-item-main-content {
	max-width: /*550px*/ 1000px !important;
}

.site-left-aligned .feed-item-main-content >* .compact-shelf,
.site-left-aligned .feed-item-main-content >* .shelf-wrapper {
	min-width: /*550px*/ 1000px !important;
}
              
.site-left-aligned #watch7-headline, #watch7-user-header, #watch7-action-buttons, #watch7-action-panels, #watch-discussion { border-left-width: 0 !important; }
.site-left-aligned #watch-discussion { border-bottom-width: 0 !important; }

.site-left-aligned #guide .collapsible-guide .guide-module-toggle {
	margin-top: 30px !important;
}

.site-left-aligned .collapsible-guide #guide-main .guide-module-content {
	background-color: white !important;
}
/* SITE LEFT ALIGNED END */
}
