css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.de") {

div [id="mngb"],
div [id="top_nav"] {
  z-index: 100 !important;
}

.gb_jb {
z-index: 100 !important;
}

#top_nav {
width: 100% !important;
}

.gb_0a, .gb_fa, .gb_ja {
    background: none repeat scroll 0% 0% rgb(241, 241, 241) !important;
}

.ads-label {
    display: none !important;
}
.ads-container-list {
    display: none !important;
}

.gb_Ta {
width: 100% !important;
}

div#mngb {
width: 100% !important;
position: fixed !important;
top: 0px !important;
left: 0px !important;
}
div#top_nav {
position: fixed !important;
top: 60px !important;
left: 0px !important;

}

div#cnt {
margin-top: 120px !important;
}

.gb_Ta, #gbq1, .gb_ba, .gb_cb, #gb {
background: none repeat scroll 0% 0% rgb(255, 255, 255) !important;
}

}
