css:
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

A.new_post_label > I {height: 90px}
.new_post_label { font-size: 0 !important }
  /** hide the label text **/

.new_post_label > I:before { content: none !important }
  /** hide the default icons **/

.new_post_label > I {
  background: url('http://i.imgur.com/6und7mf.png') no-repeat !important;
  background-size: auto !important;
  z-index: -1;
  transition: background-position .08s linear !important;
}

.new_post_label:nth-child(1) > I {background-position: 0px 0px !important}
.new_post_label:nth-child(2) > I {background-position: -73px 0px !important}
.new_post_label:nth-child(3) > I {background-position: -146px 0px !important}
.new_post_label:nth-child(4) > I {background-position: -219px 0px  !important}
.new_post_label:nth-child(5) > I {background-position: -292px 0px !important}
.new_post_label:nth-child(6) > I {background-position: -365px 0px !important}
.new_post_label:nth-child(7) > I {background-position: -438px 0px  !important}

.new_post_label:nth-child(1):hover > I {background-position: 0px -4px !important}
.new_post_label:nth-child(2):hover > I {background-position: -73px -4px !important}
.new_post_label:nth-child(3):hover > I {background-position: -146px -4px !important}
.new_post_label:nth-child(4):hover > I {background-position: -219px -4px  !important}
.new_post_label:nth-child(5):hover > I {background-position: -292px -4px !important}
.new_post_label:nth-child(6):hover > I {background-position: -365px -4px  !important}
.new_post_label:nth-child(7):hover > I {background-position: -438px -4px  !important}

.new_post_label:nth-child(1):active > I {background-position: 0px 0px !important}
.new_post_label:nth-child(2):active > I {background-position: -73px 0px !important}
.new_post_label:nth-child(3):active > I {background-position: -146px 0px !important}
.new_post_label:nth-child(4):active > I {background-position: -219px 0px !important}
.new_post_label:nth-child(5):active > I {background-position: -292px 0px !important}
.new_post_label:nth-child(6):active > I {background-position: -365px 0px !important}
.new_post_label:nth-child(7):active > I {background-position: -438px 0px !important}
}
