  border-radius: 20px !important;  
}
#xkit-control > a {
  background-color:rgba(147,121,217,0) !important;
  border-radius: 20px !important;
}



DIV.tab.iconic .tab_notice {
    background-color: #1F9C8B !important; 
    border-color:  #000 !important; 
}
