    color:  #000 !important; 
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 147px !important;
  height: 50px !important;
  background-repeat: no-repeat;
  background: url("http://i.imgur.com/PqsAOca.png")!important;
}



.identity-legacy .search_query {
    background-color: rgba(15,15,15,.5)!important;
}
.mceContentBody {
  background: rgb(255, 255, 255) !important; 
  color: rgb(68, 68, 68) !important; 
}



#new_post_label_text {
    color: rgba(62,62,64,1) !important;
}

#new_post_label_photo {
    color: rgba(194,85,60,1) !important;
}

#new_post_label_quote {
    color: rgba(218,138,45,1) !important;
}

#new_post_label_link {
    color: rgba(79,171,130,1) !important;
}

#new_post_label_chat {
    color: rgba(74,142,187,1) !important;
}

#new_post_label_audio {
    color: rgba(151,113,179,1) !important;
}

#new_post_label_video {
    color: rgba(105,116,127,1) !important;
}

#posts .post .post_content {
  background: transparent !important;
}


#posts .post {
  background: rgba(15,15,15,.4) !important;
  border-radius: 5px !important;
}

.post_controls .post_control{
 background-color: rgba(19,108,96,.7) !important;
 border: 0px solid #07a9c1 !important;
 border-radius: 9px!important;
  color: #000 !important;
}

.post .post_avatar .avatar-wrapper, .post .post_avatar, .post .avatar-wrapper {position: absolute !important;}.post .post_avatar {top:0px!important;left: -80px !important;}
 


.post .post_info .reblog_icon {background: -397px -93px url('http://i.imgur.com/uP6rYMS.png') no-repeat!important; opacity: 1!important}
 
.post .post_source_name_prefix {color:#136C60!important}

.controls_section.user_list li .follow_list_item_blog .tumblelog_title{
  color: rgba(31,156,139,1) !important;
}

.controls_section.user_list li .follow_list_item_blog.has_follow_button:before{
visibility: hidden !important;
}

.popover_menu_item {
  background: rgba(20,20,20,1) !important;
  border-bottom: 1px solid #136c60 !important;
  
}



.note_item {
    background-color: #050505 !important;
    border-radius: 15px !important;
    border: hidden !important;
}

.post_full.is_note .answerer .name, .post_full.is_note .asker .name, .post-composer_note-post .speaker .name {color: #1F9C8B!important}
.post_full.is_note .nipple, .post_full.is_note .nipple::after, .post-composer_note-post .nipple, .post-composer_note-post .nipple::after {border-left-color: #1F9C8B !important}

.post.fan_mail {
    color: #000 !important;
}

.quote {
    color: #fff !important;
}


.link_text_container{
    background-color: #1F9C8B !important;
}
  
.cog{background:transparent url('http://i.imgur.com/LQw1liB.png') -40px 0 no-repeat!important}
.compose-button{color:#000!important;background:#1F9C8B!important;border-color:#000!important}
.glass.blue, #hello_glass.blue, #glass_overlay.blue {background: rgba(20,20,20,.5)!important}
.plexi.color.active.show {
  background-color: rgba(20,20,20,.5) !important;
}
.drop_target {
  background-color: rgba(20,20,20,.5) !important;
}
.drop_zone {
  border: solid 2px rgba(19,108,96,1) !important;
  border-radius: 2px !important;
}
#tumblelog_select > .txt {
  color: rgba(143,143,143,1) !important;
}
#tumblelog_select > .txt:hover,
.photo_field.drop_zone_container .drop_zone .url_button {
  color: rgba(143,143,143,1) !important;
}
.chrome.flat.close:hover,
.photo_field.drop_zone_container .drop_zone .url_button:hover {
  color: rgba(143,143,143,1) !important;
}
#tumblelog_select > .txt:hover:after,
.post_options_wrapper .cog:hover,
.chrome.blue.options {
  opacity: 1 !important;
}
#tumblelog_select > .txt:after,
.post_options_wrapper .cog {
  opacity: 1 !important;
}








#posts .post_content blockquote{border-left: solid 2px #136C60!important}

pre,pre a,pre SPAN,.quote_source pre,div pre,pre SPAN,pre a,CODE,pre small{background:rgba(0,0,0,.5)!important;color:rgba(19,108,96,1)!important;font:bold .9em "arial",consolas,courier!important;white-space:pre-wrap!important;white-space:-moz-pre-wrap!important;white-space:-pre-wrap!important;white-space:-o-pre-wrap!important;word-wrap:break-word!important}



.elevator{color:rgba(0,0,0,0)!important;background: url('http://i.imgur.com/1B20KAJ.png')!important;width:40px!important;height:42px !important;}


.chat_line:nth-child(even),PRE{background-color:rgba(255,255,255,0)!important;
color:rgba(143,143,143,1)!important}


.answer_container,
.notes LI{background:rgba(15,15,15,1)!important;
}


.discover-controls-wrapper{background-color:rgba(15,15,15,.4)!important; position:/*[[rcmbsp]]*/!important;}


li.selected, li.recessed, a.active, div.post_filter_tabs a {

  background: none !important;

}







.post.photo_post .photo, .post.photo_post .video_thumbnail {

 box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}


#content_top.content_cap,

#content_bottom.content_cap {

  visibility: hidden !important;

}

div#edit_post_controls {

  top: 20px !important;

}

#dashboard_edit_post #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  border-radius: 14px !important;

}

#auto_pagination_loader{

background: rgba(20,20,20,0) !important;

border-bottom: 0px solid #2A3B52 !important;

border-top: 0px solid #ADADAD !important;

}




.post_content,
.new_post_label,

#right_column > UL:first-child > LI:first-child > A > SMALL > DIV,


.post_controls A,

.post_info,

.post_info A,

.footer_links.with_tags.with_source_url A,

.footer_links A,

.footer_links,

.block,


#dashboard_controls A,

#dashboard_controls DIV SPAN,

.add_and_remove,

.notification.single_notification,

.notification,

.notification .hide_overflow A,

.notification.last_notification,

.notification.last_notification A,

.answer_container SPAN,

.notes,

.notes LI,

.notes_hide_link A,

.read_more_container,

#footer, #footer DIV A,

.post_question_asker,

.source_url,

.label_and_follow,

.follow, #search_query, #search_controls_button,

.posts, .followers, .messages, .drafts, .queue, .flat.no_hover DIV,

A[class^="category"],

a:link,

a[href^="http"],

a[href^="/edit"],

a[href^="/likes"],

a[href^="/new"],

a[href^="/tagged"],

a[href^="/login"],

a[href^="/docs"],

a[href^="/explore"],

a[href^="/mega-editor"],

.count, .customize, .settings,

a span[id^="note_link"],

.answer_container,

.no_posts_found,

.following, H1[id="page_heading"], div.l-content P, H1, p SMALL,

.notes_loader,

.small_links A .hide_overflow, .sorry P, .cell.lead_editor, .sidebar_nav_item A, .tag,

 H2, #posts .post .post_controls .reblog_count span{

  color: rgba(143,143,143,1) !important;

}

.post_notes{
  color: rgba(143,143,143,1) !important;
}
    
strong {
  font-weight: bold !important;
  color: #1F9C8B  !important;
}
    


a:link:hover,

A[href^="http"]:hover,

a[href^="/edit"]:hover,

a[href^="/likes"]:hover,

a[href^="/new"]:hover,

a[href^="/tagged"]:hover,

a[href^="/customize"]:hover,

a[href^="/explore"]:hover,

a[href^="/mega-editor"]:hover,

a[id^="ask_answer"]:hover,

a span[id^="note_link"]:hover,

.block_link:hover,

.following:hover, .add_and_remove:hover, #search_query:hover, #search_controls_button:hover, .settings:hover, .posts:hover, .followers:hover, .messages:hover, .drafts:hover, .queue:hover,

SMALL, .text_field.big.wide, #crushes .count, #l10n_sorry, #footer DIV A:hover, #footer DIV STRONG:hover,

BUTTON[type="submit"]:hover, .small_links A .hide_overflow:hover, .block:hover, #signup_forms_submit:hover, .sidebar_nav_item A:hover,

#posts .post .post_controls .reblog_count span:hover {

  color: rgba(1,156,139,1) !important;

}

.source_url_gradient, #toggle_reasons {

  display: none !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  border-bottom: solid thin rgba(64,64,64,1.0) !important;

}

.post_content P A,

.post_title A {

  color: rgba(143,143,143,1) !important;

}

.tag {color:rgba(1,156,139,1)!important;
}

.post_tags:after{
    visibility:hidden!important;
}


.identity-refresh .post_full .post_permalink {border-color:transparent!important;background:#136C60!important; border-radius: 10px!important}

#tumblr_radar {display: /*[[rdryn]]*/ !important;}
.identity-refresh .controls_section.follow_list.recommended_tumblelogs, .identity-refresh .controls_section.follow_list.trending_tumblelogs{display: none !important;} /*[[recomblyn]]*/


#sidebar_footer_nav {
 display: none !important;
}

