#right_column .controls_section li a,
#right_column .small_links a, .identity-refresh .controls_section a .count .identity .controls_section li.account_header, .identity .controls_section li.editors_header, .identity .controls_section li.contributors_header, .identity .controls_section li.section_header{color: #8F8F8F !important;}
.identity-refresh .controls_section li:hover{background: rgba(15,15,15,.5) !important; border-radius:5px!important}
