#right_column .small_links a:hover, .identity-refresh:hover.controls_section a:hover .count:hover .identity:hover .controls_section:hover li.account_header:hover, .identity:hover.controls_section:hover li.editors_header:hover, .identity:hover .controls_section:hover li.contributors_header:hover, .identity:hover .controls_section:hover li.section_header:hover,.identity:hover .controls_section a:hover .count:hover{color:#1F9C8B!important;}



.left_column .followers {
