css:
@-moz-document domain("www.eventhubs.com") {

/* Remove ADs */
#topleaderboard, [id^="ox_"], [src^="http://ox-"], .GoogleActiveViewClass, [id^="DfpVisibilityIdentifier_"], .gholder_rail_ad, [id*="flash_floating"], iframe[id*="emcOrigin"], [id*="ebRichFlash"], [src*="ds.serving-sys.com"], #IL_INSEARCH, #advertisement, [id*="ad_container"], [src*="ads.pubmatic.com"], .PubAdAI, .prWrap, [id^="google_ads_iframe"] {
  display: none !important;
}

/* Remove background AD */
body {
  background-color: #B0B0B0 !important;
  background-image: url('data:image/png;base64,') !important;
  background-repeat: repeat-x !important;
  background-position: left top !important;
  pointer-events: none !important;
  cursor: default !important;
}
body > * {
  pointer-events: auto !important;
  cursor: auto !important;
}

/* Remove randomly generated preload elements */
body > div[id*="0"]:nth-child(1), #site_center:nth-child(2) > div[id*="1"]:nth-child(1), #main_content_right:nth-child(2) div[id*="4"]:nth-child(1), #main_content_right:nth-child(2) > div[id*="4"]:nth-child(2), #content_container > div:nth-child(2) > div[id*="6"]:nth-child(1) {
  display: none !important;
}

/* Remove other junk */
.socialmedia, [href="http://www.eventhubs.com/subscriptions/"], [href="http://www.eventhubs.com/advertising/"], img[src$="mlglogo.png"] {
  display: none !important;
}

}
