css:
/* by hkr (dreamflame in) */
@-moz-document url("about:newtab"){
  *{
    text-shadow:0px 0px 3px rgba(0,10,20,1),0px 0px 5px rgba(0,10,20,1),0px 0px 12px rgba(0,10,20,1)!important;
    transition:opacity .4s ease-out,color .4s ease-out,background-image .4s ease-out,box-shadow .4s ease-out!important;
    outline:none!important
  }
  #newtab-scrollbox{
    background:#162040 url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") repeat fixed 0 0!important;
    /*
    box-shadow:inset 0px 0px 8px 2px rgba(0,0,0,1),inset 0px 0px 200px 10px rgba(0,0,0,1)!important;
    */
    outline:none!important;
    border:0px!important;
  }
  #newtab-grid{margin:2px 0 -4px!important}
  #newtab-grid,.newtab-row{
    overflow:visible!important;
    width:720px!important
  }
  .newtab-cell{
    outline-color:transparent!important;
    margin:8px!important;
    background:rgba(0,10,30,.2)!important;
    box-shadow:inset 0px 0px 10px rgba(0,0,0,.5),0px 0px 0px 1px rgba(200,225,255,.08),0px 0px 12px 2px rgba(200,225,255,.04)!important;
    border-radius:10px!important;
  }
  .newtab-cell:hover{outline-color:rgba(255,255,255,.6)!important}
  .newtab-cell{border-radius:9px!important}
  .newtab-link,span.newtab-thumbnail{border-radius:8px!important}
  .newtab-thumbnail{box-shadow:inset 0px 0px 10px rgba(0,0,0,.8),0px 0px 0px 1px rgba(0,0,5,.8),0px 0px 12px rgba(0,0,5,.4)!important}
  .newtab-thumbnail:hover{box-shadow:inset 0px 0px 10px rgba(180,200,255,.08),inset 0px 0px 0px 1px rgba(180,200,255,.1),0px 0px 12px rgba(0,0,5,1)!important}
  .newtab-link{
    outline-color:transparent!important;
    background-color:#162040!important;background-image:radial-gradient(farthest-corner at center bottom,hsla(220,80%,62%,1),hsla(230,80%,32%,0) 60%),linear-gradient(rgba(30,60,175,.1),rgba(25,55,255,.3) 60%,rgba(90,120,255,.4)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
    background-size:100% 40px,100% 100%,100% 100%!important;background-repeat:no-repeat,no-repeat,repeat!important;background-position:bottom center,0 0,0 0!important;background-attachment:local,local,local!important;
    /*
    box-shadow:inset 0px 0px 30px rgba(0,10,20,1),0px 0px 10px rgba(0,10,20,1)!important;
    */
    border:1px solid rgba(0,0,0,1)!important;
    opacity:1!important;
  }
  .newtab-link:focus{box-shadow:inset 0px 0px 30px rgba(200,225,255,.3),0px 0px 10px rgba(0,10,20,1)!important}
  .newtab-link:hover,.newtab-link:focus{opacity:1!important}
  .newtab-title{
    box-shadow:0 0 0 1px hsla(220,100%,70%,.24) inset,0px 0px 10px #000!important;
    background-color:transparent!important;background-image:radial-gradient(farthest-corner at center bottom,#9fdfff,hsla(220,100%,60%,0)),linear-gradient(rgba(0,0,0,.3),#000)!important;
    height:26px!important;
    padding:3px 10px 0 12px!important;
    border:none!important;
    border-top:1px solid rgba(0,0,0,.7)!important;
    border-radius:0px 0px 6px 6px;
    color:hsla(215,100%,82%,1)!important;
    text-shadow:-1px 0 #000,0 1px #000,1px 0 #000,0 -1px #000,2px 2px 3px hsla(215,100%,90%,.4),-2px -2px 4px hsla(215,100%,5%,.6)!important;
    opacity:.8!important;
  }
  .newtab-title .favicon{margin:-1px 6px 3px 0!important}
  .newtab-link:hover .newtab-title{background-color:white!important;color:hsla(215,30%,16%,1)!important;box-shadow:0 0 0 1px hsla(220,100%,80%,.6) inset,0px 0px 10px #000!important}
  .newtab-control{opacity:.3!important}
  .newtab-site:hover>.newtab-control{opacity:1!important}
  .newtab-site[dragged]{background-color:transparent!important}
  #newtab-recent-outer{margin:-1px 0 -9px -4px!important;padding:5px 0 5px 0}
  #newtab-recent-outer *{
    color:hsla(215,100%,82%,1)!important;
    text-shadow:-1px 0 #000,0 1px #000,1px 0 #000,0 -1px #000,2px 2px 3px hsla(215,100%,90%,.4),-2px -2px 4px hsla(215,100%,5%,.6)!important;
  }
  #newtab-recent>span{margin:0 10px 0 2px!important}
  #newtab-recent-outer .recent{
    background:linear-gradient(hsla(220,100%,92%,.4),hsla(220,50%,32%,.2)) no-repeat!important;
    box-shadow:inset 0 0 5px 0 rgba(255,255,255,.12),inset 0 0 0 1px rgba(255,255,255,.2),0 0 3px 0 rgba(0,0,0,1),0 0 0 1px rgba(0,0,0,.5)!important;
    border-radius:0!important;
    margin:0 1px!important
  }
  #newtab-recent-outer .recent:hover{
    background:linear-gradient(hsla(220,100%,92%,.8),hsla(220,50%,32%,.4)) no-repeat!important;
    box-shadow:inset 0 0 5px 0 rgba(255,255,255,.4),inset 0 0 0 1px rgba(255,255,255,.2),0 0 6px 0 rgba(0,0,0,1),0 0 0 1px rgba(0,0,0,.5)!important;
  }
  #newtab-recent-outer .recent:first-of-type{border-radius:3px 0 0 3px!important}
  #newtab-recent-outer .recent:last-of-type{border-radius:0 3px 3px 0!important}
  #launcher{
    padding:8px 0 4px 0!important;
    background-color:transparent!important;background-image:radial-gradient(farthest-side at center bottom,#000,transparent),radial-gradient(farthest-corner at center top,hsla(220,100%,80%,.6),hsla(0,100%,0%,0)),linear-gradient(transparent,hsla(220,5%,0%,.8))!important;
    background-size:100% 3px,90% 2px,100% 100%!important;background-repeat:no-repeat,no-repeat,no-repeat!important;background-position:center 0,center 3px,bottom center!important;background-attachment:local,local,local!important;
    box-shadow:none!important;
    border:none!important
  }
  #config-wrapper.shown{overflow:visible!important}
  #config{
    background-color:transparent!important;background-image:linear-gradient(to right,hsla(220,5%,0%,.5),hsla(220,5%,0%,.9))!important;
    background-size:100% 100%!important;background-repeat:no-repeat!important;background-position:0 0!important;background-attachment:local!important;
    box-shadow:0 0 0 1px hsla(220,100%,80%,.2) inset,0 0 0 1px rgba(0,0,0,1),0 0 10px rgba(0,0,0,1)!important;
    border:none!important
  }
  #config *{text-shadow:-1px 0 #000,0 1px #000,1px 0 #000,0 -1px #000,2px 2px 3px rgba(0,0,0,.7),-2px -2px 4px hsla(215,100%,5%,.6)!important}
}
