@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain(docs.unity3d.com),
url-prefix("http://docs.unity3d.com"),
regexp('file:///.*Unity.*/Documentation/.*')

{
.codeExampleJS	{ font-family: Monaco, Courier, monospace !important; }
.codeExampleCS	{ font-family: Monaco, Courier, monospace !important; }
.codeExampleBOO	{ font-family: Monaco, Courier, monospace !important; }
.codeExampleRaw	{ font-family: Monaco, Courier, monospace !important; }

.codeExampleJS 	a { font-family: Monaco, Courier, monospace !important; }
.codeExampleCS 	a { font-family: Monaco, Courier, monospace !important; }
.codeExampleBOO a { font-family: Monaco, Courier, monospace !important; }
