css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dhmholley.co.uk") {
body {
    font-family: Arial, sans-serif;
    background: #12130b url("http://i.imgur.com/qanzKU3.jpg") no-repeat center center fixed;
    color: #f2f3ee;
    background-size: cover;
	padding: 0;
	margin: 0;
}

#header {
    text-align: center;
	background: rgba(40,28,46,.5);
}

h1, #ruler {
    width: 100%;
	padding-bottom: 2%;
}

#stripInner { top: 5px; right: 5px;}

#stripInner a {
    color: #949b3d;
}

#stripInner a:hover, a:visited {
    color: #828836;
}

button {
    outline: none;
    margin: 5px;
    border: 1px solid #949b3d;
    background: #8e943a;
    transition: all .20s;
    border-radius: 2px;
    color: #f2f3ee;
    padding: 5px 6px 4px 6px;
    text-decoration: none;
    text-shadow: 0px 1px 1px #001121;
}

button:hover {
    border: 1px solid #949b3d;
    background: #9fa641;
    text-decoration: none;
    cursor: pointer;
}

button:disabled {
    border: 1px solid #e8503c;
    background: #D64937;
    color: #ccc;
}

#resources {
	width: 45%;
	margin-right: 1%;
	margin-left: 2%;

	}
	
#population {
	width: 45%;

	margin-left: 2%;
	}

/* Generic container rules */

#resourcesContainer, #populationContainer, #jobsContainer, #eventsContainer, #pantheonContainer, #permaUpgradeContainer, #achievements {
	padding: 10px 10px 10px 20px;
	background: rgba(92,92,42,.5);
	margin-top: 2%;
	}
	
/* Resources Container */

#resourcesContainer {

	}
	
/* Building Panes */

#panesSelectors {
	background: rgba(92,92,42,.5);
	margin-top: 2%;
	padding: 0;
	}
	
#selectors {
	margin-top: 0;
	padding: 0;
	}
	
#buildingsPane, #upgradesPane, #deityPane, #conquestPane {
	padding: 10px 10px 10px 20px;
	}

.paneSelector {
	height: 50px;
    border: none;
    background: #281c2e;
    width: 25%;
	margin-top: 0;
	font-size: 1.5em;
}

.paneSelector:hover { background: #372740; text-decoration: none; }

.selected {
    background: #3e2c47;
}

/* Population and Jobs */

#populationContainer {

	}
	
#jobsContainer {
	margin-top: 0;
	}
	
/* Events */

#eventsContainer {
	background: rgba(40,28,46,.5);
	}

/* YE GODS */

#pantheonContainer {

	}
	
#permaUpgradeContainer {
	margin-top: 0;
	}

/* the rest */

#settings, #stats {
	margin-top: 2%;
	}
	
#settings { padding-left: 20px; width: 45%; }
	
#achievements {
	background: none;
    margin-bottom: 100px;
    text-size: 1em;
}

.achievement {
    border: none;
    margin: 0 2px;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}

.achUnlocked {
    background: #8e943a;
    font-size: 0.8em;
    padding: 4px 0 0 0;
    text-shadow: 0px 1px 1px #001121;
    height: 46px;
    text-align: center;
    -moz-border-radius: 2px;
    border-radius: 2px;
}
}
