css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("linux.org.ru") {
body {
  background-image: url("http://fc06.deviantart.net/fs71/i/2012/299/5/9/princess_luna_by_ostfront-d5j1k17.png") !important;
  background-size: cover !important;
  background-attachment: fixed !important;
  color: #CCCCCC !important;
  max-width: 100% !important;
}
#hd, #bd, #ft {
/* Use absolute width since font sizes vary between different divs. Original value: 89em */
max-width: 1235px !important;
box-sizing: border-box;
-moz-box-sizing: border-box;
-webkit-box-sizing: border-box;
margin-left: auto !important;
margin-right: auto !important;
}
#sitetitle {
color: #000000 !important;
}
div.boxlet {
background-color: rgba(39, 44, 45, 0.5) !important;
}
#hd, h2, h1, .infoblock, #whois_userpic, #contents, #user-menu, .page-number{
background-color: rgba(39, 44, 45, 0.25) !important;
}
article, #bd, #ft, table, table th, #wiki-page, #nav-menu, #tab_menu li, .tag, #related-topics, .code, pre{
background-color: rgba(39, 44, 45, 0.5) !important;
}
.tag {
text-decoration: none !important;
}
a {
color: #D6D6D6 !important;
}
a.current {
background-color: rgb(186, 189, 182) !important;
color: rgb(0, 0, 0) !important;
}
table td, table th{
border-color: #AFAAAA !important;
}
.menu a {
display: block !important;
background-color: rgba(39, 44, 45, 0.25) !important;
color: rgb(233, 185, 110) !important;
}
.news, #bd {
border-radius: 10px !important;
}
#hd {
border-bottom-left-radius: 10px !important;
border-bottom-right-radius: 10px !important;
}
#ft {
border-top-left-radius: 10px !important;
border-top-right-radius: 10px !important;
}
h2, h1 {
border-radius: 5px !important;
}
.quote {
border-color: #B9B9B9 !important;
}
.stars, .icon-tag {
color: #E4E4E4 !important;
}
#bd h2 {
padding-left: 1em !important;
}
.code pre {
background-color: rgba(39, 44, 45, 0) !important;
}
}
