css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("lawblog.de") {

/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Kleiner (Um)Stylish für LawBlog.de        */
/*                                           */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */


.one-column #page {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Textbreite                                */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    max-width: 800px !Important;
}

.commentlist > li.comment {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Gepunktete Linie und Farben weg           */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    background: none repeat scroll 0% 0% rgb(228, 233, 241) !important;
    border: 0px solid rgb(100, 100, 100) !important;
    border-radius: 0px !important;
    margin: 0px 0px 0.0em !important;
    padding: 0.1em !important;
    position: relative;
    font: 300 12px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
    border-top-width:1px !Important;
    border-top-style:dotted !Important;
    border-top-color:#808080 !Important;
}

body, input, textarea {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Alles Schwarz + Fonts                     */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    font: 300 12px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
    color: rgb(0, 0, 0) !Important;
}
span {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Alles Schwarz + Fonts                     */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    font: 300 12px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
    color: rgb(0, 0, 0) !Important;
}
.date-posted {
    line-height: 1.0em !Important;
    font: 300 15px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !Important;
    color: rgb(111, 119, 138) !important;
    font-size: 12px !important;
}

.entry-title {
    font: 300 15px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !Important;
    color: rgb(111, 119, 138) !important;
    font-size: 18px !important;
    line-height: 1.0em !Important;
}

.entry-content {
    font: 300 12px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Text mit Zeilenumbruch                    */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    -moz-hyphens: auto !Important;
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Text als Blocksatz                        */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/*    text-align: justify !Important;        */
}


#respond input[type="text"], #respond textarea {
    color: rgb(0, 0, 0) !Important;
}

.entry-meta {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Kommentar nach rechts                     */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    text-align: right !Important;
}

.reply
{
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Antwortenknopf nach rechts                */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    text-align: right !Important;
}

#branding img {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Kopf Logo verkleinern                     */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    height: 200px !Important;
}

#site-description {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Ausblenden zweite Überschriftzeile        */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
  height: 0px !Important;
  text-indent: -9999px !Important;
}

#site-title a {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* law blog "logo" text                      */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    font: 300 50px/1.625 "Palatino Linotype",Georgia !important;
    color: rgb(255, 255, 255);
    line-height: 20px !Important;
}

#respond input#submit {
    color: rgb(255, 255, 255) !Important;
}

#atr_tt
{
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Eingeblendeter Original Text              */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
     border: 2px solid rgb(150, 150, 150) !important; 
     margin: 15px 15px 0.5em !important;
     padding: 0.3em 0.3em 5.0em !important;
     color: rgb(0, 0, 0) !Important;
     font: 300 11.5px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
     max-width: 850px !Important;
     height: auto !Important;
    -moz-hyphens: auto !Important;

}

.comments-count {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Kommentarzähler nach links                */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
    left: -35px !Important;
    top: 0px !Important;
}

#reply-title {
    font-size: 18px !Important;
    border-top-width:1px !Important;
    border-top-style:dotted !Important;
    border-top-color:#808080 !Important;

}

#supplementary {
    border-top-width:1px !Important;
    border-top-style:dotted !Important;
    border-top-color:#808080 !Important;
}

blockquote 
{
  background-image: -moz-linear-gradient(rgb(242, 242, 242), rgb(242, 242, 242)  100%) !Important;
    border-top-color:#B0B0B0 !Important;
    border-top-style:solid !Important;
    border-top-width:1px !Important;
   border-left-color:#B0B0B0 !Important;
    border-left-style:solid !Important;
    border-left-width:1px !Important;
     font: 300 11.5px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
}

blockquote>p {
    background: none repeat scroll 0% 0% rgb(242, 242, 242) !important;
    border-color:#808080 !Important;
    font: 300 11.5px/1.625 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
/*    font-family: Georgia,"Bitstream Charter",serif; */
     padding: 0.2em !important;
 }

.wp-flattr-button {
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
/* Alternativ hier Flatter abschalten        */
/* =¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤=¤= */
 text-align: right !Important;
 height: 0px !Important;
 width: 570px !Important;
 }

.comment-notes
  {
     color: rgb(0, 0, 0) !Important;
  }

  
}
