css:
@-moz-document url-prefix("http://seiga.nicovideo.jp/seiga/") {
body {
    background:#fff !important;
    overflow:auto !important;
}





/* Header Illust Info
------------------------- */
#detail {
    width:986px !important;
    border-radius:0 !important;
    box-shadow:none !important;
    background:transparent !important;
    border-top:dashed 2px #ccc !important;
    margin-bottom:30px !important;
}
.im_head_bar .inner {
    width:986px !important;
    min-height:70px !important;
    padding:3px 0 74px !important;
}
.new_ver .im_head_bar .inner {
	padding-bottom:15px !important;
}
.im_head_bar .inner .other_info {
    margin-bottom:0 !important;
}
.im_head_bar .inner h1.title {
    font-size:16px !important;
    line-height:22px !important;
    color:#333 !important;
    padding-left:12px !important;
    padding-bottom:3px !important;
    border-left:solid 2px #333 !important;
    border-bottom:solid 1px #333 !important;
    margin-bottom:3px !important;
    position:relative !important;
    word-break:break-all !important;
    width:750px !important;
}
.im_head_bar .inner .discription {
    width:764px !important;
}
.im_head_bar .inner .user {
    box-shadow:none !important;
}
.im_head_bar .inner .user ul .user_link {
    border:dashed 1px #000 !important;
    padding:2px !important;
    background:#fff !important;
    width:190px !important;
    margin-top:-15px !important;
}
.im_head_bar .inner .user ul .user_link a {
    display:inline;
    border-radius:0 !important;
    border:none !important;
    padding-right:0 !important;
    word-break:break-all !important;
    white-space:normal !important;
}
.im_head_bar .inner .user ul .user_link a:hover {
    color:#000 !important;
    background:transparent !important;
}
.im_head_bar .inner .user ul .user_link a:after {
    content:"";
    display:block;
    clear:both;
}
.im_head_bar .inner .user ul .user_link a img {
    border-radius:0 !important;
    width:64px !important;
    height:64px !important;
    margin-right:5px !important;
    vertical-align:top !important;
    float:left !important;
}
.im_head_bar .inner .user ul .user_link a:hover img {
    opacity:1 !important;
}
.im_head_bar .inner .user .user_favorite {
    top:auto !important;
    right:auto !important;
    bottom:5px !important;
    left:75px !important;
}
.im_head_bar .inner .user .user_favorite .btn.favorite {
    width:auto !important;
    height:auto !important;
    background:#ffd6c6 !important;
    text-indent:0 !important;
    color:#000 !important;
    border:solid 1px #e26444 !important;
    border-radius:5px !important;
    text-shadow:none !important;
    font-weight:normal !important;
    font-size:12px !important;
    padding:2px 3px !important;
    box-shadow:none !important;
}
.im_head_bar .inner .user .user_favorite .btn.favorite:hover {
    background:#fff5c6 !important;
}
.im_head_bar .inner .user .user_favorite .btn.favorite:before {content:"お気に入りに登録";}
.im_head_bar .inner .user .user_favorite .btn.favorite span {display:none !important;}





/* Tag Coner
------------------------ */
.tag {
    border:none !important;
    border-radius:0 !important;
    margin:0 5px 0 0 !important;
    background:transparent !important;
}
.tag ul {
    line-height:20px !important;
}
.tag a.text {
    padding:0 !important;
    color:#000 !important;
    text-decoration:underline !important;
}
.tag a.text:hover {
    color:#000 !important;
    text-decoration:none !important;
}
.tag ul li > a, .tag ul li > span {
    border-width:0 !important;
    padding:0 0 0 3px !important;
    width:13px !important;
}
.icon_disable_dic {
    width:13px !important;
    height:13px !important;
    background:url("http://seiga.nicovideo.jp/img/tag/dic_off.png") !important;
}
.icon_dic {
    width:13px !important;
    height:13px !important;
    background:url("http://seiga.nicovideo.jp/img/tag/dic_on.png") !important;
}
.btn.min {
    color:#c00 !important;
    padding:0 !important;
    line-height:20px !important;
    border:none !important;
    box-shadow:none !important;
	background:transparent !important;
    text-decoration:underline !important;
    text-shadow:1px 1px #ccc !important;
}
.btn.min:hover {
    color:#c00 !important;
    text-decoration:none !important;
}
.btn.min:before {content:"【";}
.btn.min:after {content:"】";}
#tag_list_edit {
    padding:5px !important;
    border-color:#555 !important;
    border-top:none !important;
    border-left:none !important;
    border-right:none !important;
    border-radius:0 !important;
}
.illust_tag_container {
    width:660px !important;
    overflow:hidden !important;
}
.illust_main .illust_wrapper .illust_tag > ul {
    padding-left:0 !important;
    border-left:none !important;
}
.illust_main .illust_wrapper .illust_tag h2 {
    margin:0 5px 0 0 !important;
}
.illust_main .illust_wrapper .illust_tag.edit > ul li.btn,
.illust_main .illust_wrapper .illust_tag.static li.btn {
    background:transparent !important;
}
.old_ver .illust_main .illust_wrapper .illust_sub_info {
    position:absolute !important;
    top:-74px !important;
    left:0px !important;
    z-index:0 !important;
    width:100% !important;
    height:auto !important;
    max-height:72px !important;
    padding-top:0 !important;
    overflow:hidden !important;
    background:rgba(255,255,255,0) !important;
}
.old_ver .illust_main .illust_wrapper .illust_sub_info:after {
    content:"";
    display:block !important;
    position:absolute !important;
    top:73px !important;
    left:0 !important;
    width:660px !important;
    height:1px !important;
    box-shadow:0 -3px 4px 2px rgba(0,0,0,0.2) !important;
    visibility:visible !important;
}
.old_ver .illust_main .illust_wrapper .illust_sub_info:hover {
    transition:all 0s linear 0.5s !important;
    max-height:9999px !important;
    z-index:999 !important;
    background:rgba(255,255,255,0.74) !important;
    overflow:auto !important;
}
.old_ver .illust_main .illust_wrapper .illust_sub_info:hover:after {
    display:none !important;
}
.illust_main .illust_wrapper .content_tree {
    padding:2px 0 0 5px !important;
    margin-top:2px !important;
}
.old_ver .illust_main .illust_wrapper .pop {
    top:1px !important;
    right:0 !important;
    left:auto !important;
    bottom:auto !important;
    padding:0 !important;
    width:200px !important;
    height:37px !important;
    box-shadow:none !important;
    border:solid 1px #ccc !important;
}
.old_ver .illust_main .illust_wrapper .pop p.btn_close {
    position:absolute !important;
    top:auto !important;
    bottom:2px !important;
    z-index:10 !important;
}
.old_ver .illust_main .illust_wrapper .pop h2 {
    font-size:12px !important;
    bottom:0 !important;
    left:auto !important;
    right:25px !important;
    width:auto !important;
    height:16px !important;
    line-height:16px !important;
    background:transparent !important;
    color:#333 !important;
    text-shadow:none !important;
}
.old_ver .illust_main .illust_wrapper .pop a {
    position:absolute !important;
    top:0 !important;
    left:0 !important;
    width:100% !important;
    display:block !important;
    text-align:left !important;
}
.old_ver .illust_main .illust_wrapper .pop a img {
    height:37px !important;
    margin-right:5px !important;
    float:left !important;
}
.old_ver .illust_main .illust_wrapper .pop a p {
    display:inline-block;
    white-space:nowrap;
    text-overflow:ellipsis;
    word-wrap:normal;
}
.illust_main .illust_sub_info .content_tree ul .date {
    color:#555 !important;
}
.message_target .btn.min {}
.text_count {
    color:#555 !important;
}
.detail .illust_info .illust_tag.edit > ul li.add_tag form input#tags {
    margin-top:3px !important;
    margin-bottom:5px !important;
    font-size:12px !important;
    line-height:12px !important;
    height:12px !important;
    border-color:#333 !important;
    border-radius:3px !important;
}
.detail .illust_info .discription {
    font-size:13px !important;
    margin-bottom:0 !important;
}
.detail .illust_info .other_info {
    position:absolute !important;
    top:5px !important;
    left:0 !important;
}
.detail .illust_info .content_tree {
    padding:5px 0 0 !important;
}
.detail .illust_info .content_tree ul li {display:inline-block;}
.illust_main {
    margin:0 auto 10px !important;
}
.illust_main .illust_wrapper {
    padding:10px 22px !important;
    border-right:none !important;
}
.illust_main .illust_side {
    border-left:none !important;
    padding:0 !important;
}
.old_ver .illust_main .illust_side .illust_count {
    padding:3px 0 !important;
    border-width:0 0 1px !important;
    border-color:#ccc !important;
}
.old_ver .illust_main .illust_side .illust_count li {
    font-size:13px !important;
    color:#333 !important;
    margin:0 5px 0 0 !important;
    text-align:left !important;
}
.old_ver .icon_view_big,
.old_ver .icon_comment_big,
.old_ver .icon_clip_big {
    margin-top:-3px !important;
    background:transparent !important;
    text-indent:0 !important;
    width:auto !important;
    height:auto !important;
}
.old_ver .icon_view_big:before {content:"閲覧:";}
.old_ver .icon_comment_big:before {content:"コメント:";}
.old_ver .icon_clip_big:before {content:"クリップ:";}





/* Comment List
---------------------------- */
.illust_main .illust_side .illust_count li span.txt {display:none !important;}
.illust_main .illust_side .illust_comment {
    padding:10px 0 !important;
}
.illust_main .illust_side .illust_comment .comment_list {
    width:100% !important;
}
.illust_main .illust_side .illust_comment .title_bar .comment_more_link {
    margin-top:-15px !important;
}
.illust_main .illust_side .illust_comment .title_bar .ng_comment_link {
    margin-top:-12px !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info {
    text-align:left !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item {
    border-bottom:dotted 1px #333;
    padding:2px 0 !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li {
    color:#333 !important;
    font-size:12px !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li.new {
    padding:0 !important;
    background:transparent !important;
    width:auto !important;
    color:#c00 !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li.user {
    position:static !important;
    color:#ff8c00 !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info {
    word-break:break-all;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li.count_new {
    top:-9px !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li.count_new:after {content:"!";}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li.text {
    font-size:13px !important;
    padding:0 0 0 4px !important;
    border:none !important;
    background:transparent !important;
    box-shadow:none !important;
    margin-bottom:0 !important;
}
.illust_main .illust_side .illust_comment .comment_list .comment_list_item .comment_info li.text:after {
    display:none !important;
}
.related_info .sub_info_side .share {
    padding:10px 3px 7px !important;
}
.related_info .sub_info_side .share div.blog ul .btn.blog {
    background:transparent !important;
    font-size:12px !important;
    width:100px !important;
    margin-left:-12px !important;
}
.illust_main .illust_side .res {
    padding:5px 0 6px;
}
.illust_main .illust_side .res .inner form .comment_post .comment_text .content {
    height:20px !important;
    padding:0 0 0 5px !important;
    font-size:13px !important;
    line-height:20px !important;
    width:315px !important;
    margin-bottom:5px !important;
}
.over_ray {
    display:none !important;
}

.comment_all {
    box-shadow:none !important;
    background:#fff !important;
    position:fixed !important;
    top:36px !important;
    left:50% !important;
    bottom:0 !important;
    width:336px !important;
    height:auto !important;
    z-index:9998 !important;
    margin-left:155px !important;
    border-left:solid 1px #ccc !important;
    border-right:solid 1px #ccc !important;
}
body.nofix .comment_all {
    top:0 !important;
    z-index:9999 !important;
}
.comment_all .comment_all_inner,
.comment_all .comment_all_inner .illust_main {
    width:336px !important;
}
.comment_all_header {
    position:absolute !important;
    top:-1px !important;
    left:0 !important;
    width:336px !important;
    background:#fff !important;
    box-shadow:none !important;
    padding:0 !important;
    height:30px !important;
    border-bottom:solid 1px #ccc;
    margin-left:0 !important;
}
.comment_all .comment_all_header .controll {
    top:5px !important;
}
.comment_all_header h2 {
    display:none !important;
}
.comment_all_inner .illust_main .illust_wrapper {
    padding:0 !important;
}
.comment_all .comment_all_inner .illust_main {
    margin:0 !important;
}

.comment_all .comment_all_inner .illust_main .illust_wrapper .inner .thum_large a img {
    max-width:598px !important;
    width:auto !important;
    height:auto !important;
}
.comment_all .comment_all_inner .illust_main .illust_wrapper {
    display:none !important;
}
.comment_all .comment_all_inner .illust_main .illust_side {
    position:absolute !important;
    left:0 !important;
    top:30px !important;
    width:336px !important;
    background:#fff !important;
    overflow:visible !important;
    border:solid 1px #ccc !important;
    padding:0 !important;
    border-width:0 !important;
}
.comment_all .comment_all_inner .illust_main .illust_side > .title_bar {
    padding:0 10px !important;
}
.comment_all .comment_all_inner .illust_main .illust_side > .title_bar .ng_comment_link {
    right:0 !important;
}
.comment_all .comment_all_inner .illust_main .illust_side .illust_comment .comment_list {
    width:100% !important;
    padding-bottom:0 !important;
}
#ko_comment_all {
    padding-top:5px !important;
    width:331px !important;
    margin-left:5px !important;
    padding-right:0 !important;
    position:relative !important;
    z-index:10000 !important;
    padding-left:0 !important;
    top:-19px !important;
}
.comment_all .comment_all_inner .illust_main .illust_side > .title_bar {
    position:relative !important;
    z-index:10000 !important;
    top:-23px !important;
    margin-top:0 !important;
    margin-bottom:0 !important;
    width:auto !important;
    display:inline-block !important;
    padding-left:15px !important;
}
.comment_all .comment_all_inner .illust_main .illust_side > .title_bar h2 {
    font-size:16px !important;
    display:inline-block !important;
    line-height:17px !important;
}
.comment_all .comment_all_inner .illust_main .illust_side > .title_bar .ng_comment_link {
    display:inline-block !important;
    position:relative !important;
    top:-3px !important;
}
.comment_all .comment_all_inner .illust_main .illust_side .illust_comment .res {
    box-shadow:none !important;
    padding:0 !important;
    background:#fff !important;
    margin-left:152px !important;
    border-left:solid 1px #ccc !important;
    border-right:solid 1px #ccc !important;
    width:336px !important;
    margin-left:155px !important;
}
.comment_all .comment_all_inner .illust_main .illust_side .illust_comment .res .inner {
    width:auto !important;
    margin:3px 5px !important;
}
@media screen and (max-width: 986px){
    .comment_all {
        left:auto !important;
        right:0 !important;
        z-index:9997 !important;
    }
    .comment_all .comment_all_inner .illust_main .illust_side .illust_comment .res {
        left:auto !important;
        right:0 !important;
        margin-left:0 !important;
    }
    #header_block {
        min-width:986px !important;
    }
    .illust_main .illust_wrapper .inner {
        position:relative !important;
        z-index:9998 !important;
    }
    .old_ver .illust_main .illust_wrapper .illust_sub_info:hover {
        z-index:9998 !important;
    }
}
.ng_comment .fukidashi.top.right {
    width:319px !important;
    right:-8px !important;
    box-shadow:none !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner h4 {
    margin-top:6px !important;
    margin-bottom:6px !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .radio_list {
    margin-bottom:3px !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner input[type="text"] {
    border-radius:3px !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list {
    width:100% !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list table {
    border-top:solid 1px #ccc !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list table tr {
    border-color:#ccc !important;
    border-width:0 0 1px 1px !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list table .top_tr {
    color:#666 !important;
    font-size:11px !important;
    padding:0 !important;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list table th,
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list table td {
    padding:1px 0 1px 5px !important;
    border-right:solid 1px #ccc;
}
.ng_comment .fukidashi.top.right .ng_comment_inner .ng_comment_list table td.delete {
    width:10% !important;
    white-space:nowrap !important;
    padding:0 5px !important;
    text-align:center !important;
}
#ko_commentng_all .ng_comment .fukidashi.top.right {
    z-index:10001 !important;
    right:-31px !important;
    width:336px !important;
}
#ko_commentng_all .ng_comment .fukidashi.top.right:before,
#ko_commentng_all .ng_comment .fukidashi.top.right:after {
    right:auto !important;
    left:92px !important;
}





/* Illust Clip Coner
------------------------------ */
#ko_clip {
    position:absolute !important;
    bottom:100% !important;
    right:0 !important;
    padding:0 !important;
    margin-bottom:2px !important;
    border:none !important;
    z-index:1000 !important;
    border-radius:0 !important;
}
.add_clip {
    width:320px !important;
    box-shadow:none !important;
}
.add_clip .select_clip {
    height:auto !important;
    padding-top:28px !important;
}
.add_clip .select_clip .select_clip_button {
    width:100px !important;
    height:28px !important;
    margin-top:-28px !important;
}
.add_clip .select_clip .clip_list {
    left:-1px !important;
    padding-top:28px !important;
    width:322px !important;
}
.add_clip .select_clip .clip_list,
.add_clip .select_clip.active .clip_list {
    bottom:auto !important;
    top:0px !important;
}

.select_clip .select_clip_button .icon_clip_add {
    margin-left:-9px !important;
    text-indent:0 !important;
}
.select_clip .select_clip_button .icon_clip_add:after {
    content:"▼";
    line-height:23px !important;
    padding-left:30px !important;
    font-size:18px !important;
    color:#0ea7d6 !important;
    text-shadow:none !important;
    cursor:default !important;
}
.select_clip.active .select_clip_button .icon_clip_add:after {
    content:"▲";
    color:#fff !important;
}
.add_clip .select_clip .clip_list .fukidashi {
    border-radius:0 !important;
    box-shadow:none !important;
    border-bottom-width:0 !important;
}
.add_clip .select_clip .clip_list .fukidashi:before,
.add_clip .select_clip .clip_list .fukidashi:after {
    display:none !important;
}
.add_clip .select_clip .clip_list .fukidashi .title_bar h2 {
    font-weight:bold !important;
    color:#000 !important;
    padding:2px 2px 2px 4px !important;
}
.add_clip .select_clip .clip_list .fukidashi .title_bar .btn_close {
    top:0 !important;
    right:0 !important;
    width:29px !important;
    height:20px !important;
    background-position:9px -1245px !important;
    transition:transform 0.3s ease 0s;
}
.add_clip .select_clip .clip_list .fukidashi ul li,
.add_clip .select_clip .clip_list .fukidashi .add_list {
    padding:0 0 0 20px !important;
    border-top:none !important;
    font-weight:normal !important;
    text-shadow:none !important;
    line-height:20px !important;
    border-color:#ccc !important;
}
.add_clip .select_clip .clip_list .fukidashi ul li:hover,
.add_clip .select_clip .clip_list .fukidashi .add_list:hover {
    background-color:#3399ff !important;
}
.add_clip .select_clip .clip_list .fukidashi ul li.selected:hover {
    background-color:#fff !important;
    cursor:default !important;
}
.add_clip .select_clip .clip_list .fukidashi ul li .text {
    -o-text-overflow:ellipsis !important;
    text-overflow:ellipsis !important;
    white-space:nowrap !important;
    overflow:hidden !important;
}
.add_clip .select_clip .clip_list .fukidashi ul li.selected .text {
    color:#3399ff !important;
}
.add_clip .select_clip .clip_list .fukidashi ul li:not(.selected):hover .text,
.add_clip .select_clip .clip_list .fukidashi .add_list:hover {color:#fff !important;}
.add_clip .select_clip .clip_list .fukidashi ul li .status {
    display:none !important;
}
.add_clip .select_clip .clip_list .fukidashi .add_list .icon_clip_new_folder {display:none !important;}
.add_clip .select_clip .clip_list .fukidashi .add_list {
