css:
@-moz-document domain("www.slobulus.com") {
body{
  width:100%;
  background: #ccc url('https://www.efortuny.es/sites/all/themes/efortuny/images/black_denim.png') !important;
  background-repeat:repeat-x repeat-y;
  overflow:hidden;
}

nav{
  position:fixed;
  width:15%;
  max-width:150px;
}

nav a{
    /*box-shadow:0px 0px 2px 2px black inset;*/
  -webkit-border-radius: 5px;
  -moz-border-radius: 5px;
  border-radius: 5px;
  background-color: rgba(27, 27, 27, 0.8) !important;
  color:white !important;
  border-color: #555 #000 #000;
  border-style: solid;
  border-width: 1px;

}

nav a:hover{
  background-color: rgba(58, 58, 58, 0.8);
  box-shadow:0px 0px 2px 2px black inset;
}
    
section{
  width:85%;
  margin-left: 150px;
}

.div_usuarios{
  color:white !important;
}

#Generacion-Actual, #Viciados-Podcast, #On-Live, #Twitter-El-rincon-de-Slobulus, #Facebook-El-rincon-de-Slobulus, #Colabora { visibility:hidden;
  opacity:0;
  transition:opacity 0.5s linear; overflow:hidden; }

#Generacion-Actual:target, #El-rincon-de-Slobulus:target, #Viciados-Podcast:target, #On-Live:target, #Twitter-El-rincon-de-Slobulus:target, #Facebook-El-rincon-de-Slobulus:target, #Colabora:target { visibility:visible;
  opacity:1;}

}
