css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(engadget.com) {
  #hat, .cobrand, #tools, #subcontent, #footer, .footer-cobrand, .bottomleader, #grid, .topleader, #outerslice, .quigoads, .inpostad, .clearfix, .sponsored, div.postbody div[style="text-align: center;"] br, div.postbody div[align="center"] br, #dogear_promo, a[href="http://www.engadget.com/2008/11/24/engadget-holiday-gift-guide-2008/"], #recentheadlines, #recentheadlines + ul, #instructions {
    display: none !important;
  }
  #logo {
    z-index: 1000 !important;
  }
  #header, #header-inner {
    height: 100px !important;
  }
  #content {
    margin-left: 5px !important;
    margin-right: 5px !important;
  }
  .searchform {
    background: transparent !important;
    width: 100% !important;
    padding-top: 32.5px !important;
  }
  .searchform form {
    margin-left: 390px !important;
    margin-right: 87px !important;
    width: auto !important;
  }
  .searchform form input.text {
    background-image: none !important;
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #E7E7E7 !important;
    width: 100% !important;
    height: 18px !important;
    padding: 4px !important;
  }
  .searchform form button {
    right: -83px !important;
  }
  #page {
    min-width: 0px !important;
    max-width: none !important;
  }
  div.postbody div[style="text-align: center;"] img, div.postbody div[align="center"] img, div.postbody object {
    float: left !important;
  }
}
