  url('http://www.tumblr.com/'),

  url-prefix('https://www.tumblr.com/'), 

  url-prefix('http://www.tumblr.com/') 
{



body {

background: url(http://25.media.tumblr.com/59e119df6b35494d05dfa9f62670699f/tumblr_mwau5lVpls1sk768qo1_r1_1280.png) bottom right no-repeat fixed !important;background-size: cover !important;


}












#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(0,0,0,0.1) !important;;
}















  


 

.content.is_media, .mask.top, .mask.bottom {

  background: none !important; /*remove ugly blue radar border frame from existence*/
}


.post.photo_post .photo, .post.photo_post .video_thumbnail  {
  border-radius: 16px !important;/*change to 10px to match the curve of the right column boxes - 16px for iconbar box*/
}


.mceContentBody {


  color: rgba(0,0,0,0.8) !important;


  background: rgba(0,0,0,0.8) !important;
}


.radar { display: none !important }
}
