css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.guitarplayer.ru") {

body {
	background-image: url("/*[[back]]*/") !important;
	background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	/*[[repeating]]*/
}
#header, #header div.frame, #content_section, #content_section div.frame, .windowbg2 span.topslice, .windowbg2 span.topslice span, .windowbg2 span.botslice span, .windowbg2 span.botslice, .windowbg span.topslice span, .windowbg span.topslice, .windowbg span.botslice span, .windowbg span.botslice, .catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th, h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg, .buttonlist ul li a, span.upperframe, span.upperframe span, .roundframe, span.lowerframe span, span.lowerframe, #footer_section, #footer_section div.frame, div.title_barIC h4.titlebg, h4.titlebg, h3.titlebg, .titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td  {
	background: none !important;
}
#yandex_ad {
	display: none !important;
}
.navigate_section ul {
	border: none !important;
}
/* text */
body, td, th, tr, button, input, .windowbg2, .windowbg, blockquote.bbc_standard_quote, select, textarea, h3.titlebg a, h3.titlebg, h4.titlebg, h4.titlebg a, .titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td, h1, h2, h3, h4, h5, h6 {
	color: white !important;
}
/* links */
a:link, a:visited, a:active {
	color: /*[[linkcolor]]*/ !important;
}
/* blocks */
#wrapper, div.cat_bar, #quickReplyOptions {
	background: none repeat scroll 0 0 rgba(0, 0, 0, 0.5) !important;
	border: none !important;
	box-shadow: 0 0 4px white;
	padding: 10px !important;
	border-radius: 10px !important;
	-webkit-border-radius: 10px !important;
	-moz-border-radius: 10px !important;
	-khtml-border-radius: 10px !important;
}
.windowbg2, .dropmenu li li a:hover {
	background: none repeat scroll 0 0 rgba(0, 0, 0, 0.3) !important;
}
.windowbg, .dropmenu li ul, .description, .description_board, .plainbox, .information {
	background: none repeat scroll 0 0 rgba(0, 0, 0, 0.4) !important;
}
/* white */
blockquote.bbc_standard_quote, input[type="text"], select, .richedit_resize, textarea, .input_password { 
	background-color: rgba(255, 255, 255, 0.1) !important;
}
/* headers */
.button_submit, .buttonlist ul li a span, input[type="submit"], input[type="button"], div.title_barIC, h4.titlebg {
	background: -moz-linear-gradient(top, #444444 0%, #222222 52%, #222222 99%) !important; /* FF3.6+ */
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#444444), color-stop(52%,#222222), color-stop(99%,#222222))  !important; /* Chrome,Safari4+ */
	background: -webkit-linear-gradient(top, #444444 0%,#222222 52%,#222222 99%)  !important; /* Chrome10+,Safari5.1+ */
	background: -o-linear-gradient(top, #444444 0%,#222222 52%,#222222 99%)  !important; /* Opera 11.10+ */
	border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	-moz-border-radius: 5px !important;
	-khtml-border-radius: 5px !important;
	text-shadow: none !important;
	padding-left: 10px !important;
}
/* IMAGES */
.attachments.smalltext img[alt="*"] {
	height: 16px !important;
	padding-right: 7px !important;
	width: 0 !important;
	background-image: url('data:image/gif;base64,R0lGODlhBwAQAIAAAP///wAAACH5BAEAAAEALAAAAAAHABAAAAIWTIB2CuvZYlyQTknP1bniu3mcATZGAQA7');
}

}
