css:
@-moz-document domain("volafile.io") {

/*[[TightIcon]]*/

/*[[ScrollSpace]]*/

/*[[HideHours]]*/

/*Don't change uploading status */
html.room #file_list .file.uploading .fstatus{display:inline-block!important}



/*Colour Things*/
mark{background:rgb(21,21,21) !important;color:rgb(114,100,172)!important}
textarea,input[type=text],input[type=password]{background:rgb(31,31,31) !important;color:rgb(119,119,119)!important}
.button,input[type=submit]{background:rgb(126,91,172) !important;color:rgb(21,21,21)!important}
.button:hover,input[type=submit]:hover{background:rgb(120,100,172) !important}
.button:active,input[type=submit]:active{background:#e8b0ff !important}
.button.green,input[type=submit].green{background:rgb(114,100,172) !important}
.button.green:hover,input[type=submit].green:hover{background:#30d376 !important}
.button.green:active,input[type=submit].green:active{background:#45d784 !important}
.toggle i{color:rgb(89,89,89)!important}
.nano>.pane>.slider{background:rgba(49,49,49,0.5) !important}
.nano>.pane>.slider:hover{background:rgb(59,59,59) !important}
Html.room #file_list .file.failed{background:red !important}
.file_tag.tag_key_user{background:rgb(44,44,44) !important;border-color:rgb(99,99,99)!important;color:rgb(139,139,139)!important}
.file_tag{background:rgb(31,31,31) !important;border-color:rgb(99,99,99)!important;color:rgb(139,139,139)!important}
.file_tag:hover{background:rgb(20,20,20) !important;color:rgb(126,91,172)!important}
html.room #overlay{background:rgb(21,21,21) !important;color:rgb(89,89,89)!important}
.highlight b{color:rgb(144,100,172)!important}
*{border-color:rgb(49,49,49)!important}
#room_name_container{color:rgb(126,91,172)!important;text-align:center!important}
#chat_messages .message.admin{background:rgb(24,24,24) !important}
.fcontrol,.fsize,.fstatus,.toggle i,.fcontrol,I.fa.fa-download.clickable{color:rgb(60,60,60)!important}
.fcontrol i.clickable{color:rgb(69,69,69)!important}
html.room #file_list .file.uploading,html.room #file_list.uneven .file.uploading:nth-child(2n+1),html.room #file_list.even .file.uploading:nth-child(2n){background:rgb(15,15,15) !important}
textarea.defaultValue,input[type=text].defaultValue,input[type=password].defaultValue,body,html.room #header #room_search #clearsearch,a,a:visited,html.room #file_list .file{color:rgb(119,119,119)!important}
.toggle.enabled i,html.index h1.logo,html.index h1.logo span,html.room #header #header_row_container a:hover i.green-icon,a:hover,html.room #header #room_search #clearsearch:hover,html.room #file_list .file .fcontrol i.clickable:hover,a:active,html.room #header #room_search #clearsearch:active,html.room #file_list .file .fcontrol i.clickable:active,html.room #chat_frame #chat_messages_frame #chat_messages .message.admin b,html.room #file_list .file.playing i.filetype,html.room #file_list .file.loading i.filetype,.file a.fname:hover,html.room #file_list .file.uploading .fstatus{color:rgb(126,91,172)!important}
html,html.room #header,div.ui_frame{background:rgb(21,21,21) !important}
html.room #header #header_row_container a i.green-icon,html.room #header #header_row_container a:active i.green-icon{color:rgb(76,186,115)!important}
html.room #chat_frame #chat_hbar,html.room #file_list.even .file:nth-child(2n),html.room #file_list.uneven .file:nth-child(2n+1){background:rgb(31,31,31) !important}
html.room #chat_frame #chat_messages_frame #chat_messages .message.highlight{background:rgb(29,29,29) !important}
html.room #chat_frame #chat_messages_frame #chat_messages .message:not(.rank_3):not(.rank_2):not(.rank_1).user b {color:#00A938!important}

}
