css:
@-moz-document domain(drupal.org) {
  * {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
  }

  #header {
    background-image: none !important;
  }

  .container-12 {
    width: 100% !important;
    max-width: 1140px !important;
  }

  .grid-12 {
    float: left;
    margin-right: 2.35765% !important;
    width: 100% !important;
  }

  .grid-12 {
    margin-right: 0 !important;
  }

  .grid-8 {
    float: left;
    margin-right: 2.35765% !important;
    width: 65.88078% !important;
  }

  .grid-8:last-child {
    margin-right: 0 !important;
  }

  .grid-4 {
    float: left;
    margin-right: 2.35765% !important;
    width: 31.76157% !important;
  }

  .grid-4:last-child {
    margin-right: 0 !important;
  }

  /* Header */
  #header-content {
    padding-top: 0 !important;
  }

  h1#site-name {
    margin: 0 !important;
  }

  #site-name {
    margin-top: -10px !important;
    margin-bottom: 25px !important;
  }

  #header-right-inner {
    margin-top: 1em !important;
  }

  #header {
    border-top: 30px solid #4C8EBC !important;
  }

  #header-screen {
    border-top: 2px solid #ff !important;
  }

  #header-inner {
    position: relative;
  }

  #nav-masthead {
    float: none !important;
    height: 30px !important;
    margin-right: 0 !important;
    position: absolute !important;
    right: 0 !important;
    text-align: right !important;
    top: -30px !important;
  }
  #nav-masthead .homepage {
    display: none !important;
  }
  #nav-masthead a {
    border-width: 0px !important;
    margin: 0 !important;
    color: #DEF0FB !important;
    padding: 0.5em 0.75em 0.5em !important;
  }
  #nav-masthead a:hover, #nav-masthead a:active {
    color: #fff !important;
  }

  #nav-masthead ul {
    display: inline;
  }
  #nav-masthead ul li {
    float: none !important;
    display: inline !important;
  }
  #nav-masthead ul li a {
    float: none !important;
    border-radius: 0px !important;
    display: inline-block !important;
    background-color: transparent !important;
  }

  #nav-header ul {
    background-color: rgba(255, 255, 255, 0.2) !important;
  }

  #userinfo {
    float: none !important;
    display: inline-block !important;
    margin: 0 !important;
  }

  /* Tabs */
  #nav-content {
    background-color: #DEF0FB !important;
    margin: 2px 0 !important;
  }
  #nav-content ul {
    border-radius: 0 !important;
    border: 0 !important;
    margin-bottom: 0 !important;
  }

  #nav-content li.active,
  #nav-content li.active-parent a {
    background-color: #fff !important;
    border-radius: 0 !important;
    border: 0 !important;
    margin-top: -0.3em !important;
    padding-top: 0.3em !important;
  }

  #nav-content li.active a.active,
  #nav-content li.active-parent a {
    padding: 0.25em 1em !important;
  }

  #page {
    margin-top: 0px !important;
  }

  /* Page title */
  #page-title-tools {
    background-color: #AED8F5 !important;
    margin: 2px 0 !important;
    padding: 0.5em 1em !important;
  }

  #page-title {
    color: #fff !important;
    font-size: 18px !important;
    margin: 0 !important;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);
  }

  .page-title-wrapper {
    margin: 0 !important;
  }

  #page-tools {
    margin: 0 !important;
  }
  #page-tools li {
    border: 0 !important;
    float: left !important;
    line-height: 1.4em !important;
    padding: 10px 10px 10px !important;
  }
  #page-tools a {
    color: #0678BE !important;
  }
  #page-tools a:hover, #page-tools a:active {
    color: black !important;
  }

  /* Header Search */
  #search-block-form {
    float: right;
  }

  #header input[type="text"] {
    border: 0 !important;
    margin: 0 !important;
    height: 35px !important;
  }

  #search-theme-form-submit {
    height: 35px;
    border-radius: 0 !important;
    margin-top: 0 !important;
  }

  #header input[type="submit"],
  #search-theme-form-submit {
    border-radius: 0 !important;
    margin: 0 !important;
    height: 35px !important;
  }

  #search-theme-form-advanced fieldset.collapsible legend a {
    color: #fff !important;
    font-size: 12px !important;
    text-transform: uppercase !important;
  }

  /*

  Dreditor changes aren't working well.
  .dreditor-button,
  .dreditor-button:link,
  .dreditor-button:visited,
  #content a.dreditor-button
  */
  .doh-link {
    background: #1281c8 !important;
    color: #fff !important;
    font-weight: bold !important;
    border-radius: 0 !important;
    border: 0 !important;
    padding: 0.231em 0.615em !important;
    box-shadow: none !important;
  }

  .doh-link:hover {
    background: #40a9ec !important;
  }

  #main {
    display: inline-block !important;
    margin-top: 10px !important;
  }

  /* Submitted */
  .submitted time {
    color: #333333;
    font-style: normal !important;
  }

  .submitted,
  #content .modified,
  #aggregator .feed-item-meta,
  #content .comment .links {
    color: #aaa !important;
    font-size: 0.8em !important;
    text-transform: uppercase;
    margin-bottom: 0.75em !important;
  }

  .submitted a {
    text-transform: none !important;
  }

  /* Number permalinks */
  .node-type-project-issue .comment .permalink {
    color: #999 !important;
    font-size: 1.5em !important;
    line-height: 1em !important;
    position: absolute !important;
    margin-top: 5px !important;
    position: absolute !important;
    text-align: right !important;
    margin-left: -110px !important;
    width: 100px !important;
  }

  /* Comments */
  .comment .submitted {
    margin-top: 5px !important;
  }

  .comment {
    position: relative;
  }

  .comment-report {
    position: absolute;
    right: 0;
    top: 5px;
  }

  .comment-report a {
    color: #aaa !important;
  }

  /* Form Styles */
  .form-item label {
    margin-bottom: 3px;
  }

  .form-text {
    padding: 4px 6px;
  }

  /* Issue status */
  #block-project-issue-issue-metadata .field-name-field-issue-status {
    color: #555555 !important;
    font-size: 18px !important;
    font-weight: normal !important;
    padding: 1em 0 0.75em !important;
  }

  #block-project-issue-issue-metadata .field {
    padding: 0 !important;
  }

  #block-project-issue-issue-metadata .field-label {
    background-color: rgba(255, 255, 255, 0.82);
    padding: 0 0.5em !important;
    text-align: right !important;
  }

  #block-project-issue-issue-metadata .field-items {
    padding: 0 0.5em !important;
  }
}
