css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com"),domain("drive.google.com") {
    .v .fY {border-bottom:1px solid #D9D9D9 !important;}
    .v .fZ,.settings-tab.settings-tab-active {
        background-color:transparent !important;
        border-bottom:3px solid #DD4B39 !important;
        font-weight:bold !important;
        color:#DD4B39 !important;
    }
    .v .fZ .f0, .v .f1 .f0 {color:inherit !important;}
    .f2 .fZ, .f2 .f1,.settings-tab {
        padding:12px 10px !important;
    }
    .v .f1,.settings-tab {
        border-bottom:3px solid transparent !important;
        font-weight:normal !important;
        color:#777 !important;
    }
    .v .f1:hover,.settings-tab:hover:not(.settings-tab-active) {
        color:#222 !important;
    }
    .v .f1:active,.settings-tab:active {
        color:#DD4B39 !important;
    }
    .r4 {
    background-color:#E5E5E5 !important;
    padding:0 !important;
    }
    .r7, .al8, .al7 {
        box-shadow: rgba(0, 0, 0, 0.0980392) 0px 2px 1px !important;
        display:block !important;
        margin:20px  !important;
        background-color:#FFF !important;
    }
    .r4, .r7, .r9, .r4>select, .r4>span:not(.qK) {
        font-family: roboto,arial,sans-serif !important;
        font-weight:normal !important;
    }
    .v .fY {
    border-top-left-radius:2px !important;
    border-top-right-radius:2px !important;
    }
    .Us,.settings-header {
        color: #999;
        font-size: 16px !important;
        margin: -5px 0px -12px 20px !important;
        min-height: 22px  !important;
        font-family: roboto,arial,sans-serif !important;
        font-weight:normal !important;
        float: left !important;
    }
    .settings-header {
    margin:5px 0 !important;
    }
    .rc {
        font-weight:400 !important;
        font-size:14px !important;
        font-family: roboto,arial,sans-serif !important;
        display:block !important;
        width:100% !important
    }
    .r4 input[type="text"] {
        -moz-appearance:none !important;
        background-color:#FFF !important;
        border-right: 1px solid rgb(217, 217, 217) !important;
        border-width: 1px !important;
        border-style: solid !important;
        border-color: rgb(192, 192, 192) rgb(217, 217, 217) rgb(217, 217, 217) !important;
        border-radius: 1px !important;
        height: 27px !important;
    }
    .r4 input[type="text"]:hover {
        border-width: 1px !important;
        border-style: solid !important;
        border-right: 1px solid rgb(185, 185, 185) !important;
        border-color: rgb(160, 160, 160) rgb(185, 185, 185) rgb(185, 185, 185) !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1) inset !important;
    }
    .r4 input[type="text"]:focus {
        border: 1px solid rgb(77, 144, 254) !important;
        outline: medium none !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.3) inset !important;
    }
    .r4 input[type="button"], .r4 input[type="submit"], .r4 button, #settings-save-button, #settings-cancel-button {
    -moz-appearance:none !important;
        border-radius:2px !important;
	background-color:#f5f5f5 !important;
	background-image:-webkit-linear-gradient(top,#f5f5f5,#f1f1f1) !important;
	background-image:-moz-linear-gradient(top,#f5f5f5,#f1f1f1) !important;
	background-image:-ms-linear-gradient(top,#f5f5f5,#f1f1f1) !important;
	background-image:-o-linear-gradient(top,#f5f5f5,#f1f1f1) !important;
	background-image:linear-gradient(top,#f5f5f5,#f1f1f1) !important;
	border:1px solid #dcdcdc !important;
	color:#444 !important;
	cursor:default !important;
	font-size:11px !important;
	font-weight:bold !important;
	line-height:27px !important;
	list-style:none !important;
	margin:0 2px !important;
	min-width:46px !important;
	outline:none !important;
	padding: 0 6px !important;
	text-align:center !important;
	text-decoration:none !important;
	font-family: Arial,sans-serif !important;
    }
    .r4 input[type="button"]:hover, .r4 input[type="submit"]:hover, .r4 button:hover, #settings-save-button:hover, #settings-cancel-button:hover {
    background-color:#f8f8f8 !important;
	background-image:-webkit-linear-gradient(top,#f8f8f8,#f1f1f1) !important;
	background-image:-moz-linear-gradient(top,#f8f8f8,#f1f1f1) !important;
	background-image:-ms-linear-gradient(top,#f8f8f8,#f1f1f1) !important;
	background-image:-o-linear-gradient(top,#f8f8f8,#f1f1f1) !important;
	background-image:linear-gradient(top,#f8f8f8,#f1f1f1) !important;
	-webkit-box-shadow:0 1px 1px rgba(0,0,0,.1) !important;
	-moz-box-shadow:0 1px 1px rgba(0,0,0,.1) !important;
	box-shadow:0 1px 1px rgba(0,0,0,.1) !important;
	border-color:#c6c6c6 !important;
	color:#333 !important
	}
	.r4 input[type="button"]:active, .r4 input[type="submit"]:active, .r4 button:active, #settings-save-button:active, #settings-cancel-button:active {
	-webkit-box-shadow:inset 0 1px 2px rgba(0,0,0,.1) !important;
	-moz-box-shadow:inset 0 1px 2px rgba(0,0,0,.1) !important;
	box-shadow:inset 0 1px 2px rgba(0,0,0,.1) !important;
	background-color:#eee !important;
	background-image:-webkit-linear-gradient(top,#eee,#e0e0e0) !important;
	background-image:-moz-linear-gradient(top,#eee,#e0e0e0) !important;
	background-image:-ms-linear-gradient(top,#eee,#e0e0e0) !important;
	background-image:-o-linear-gradient(top,#eee,#e0e0e0) !important;
	background-image:linear-gradient(top,#eee,#e0e0e0) !important;
	border:1px solid #ccc !important;
	color:#333 !important;
	}
	.r4 input[type="button"]:focus, .r4 input[type="submit"]:focus, .r4 button:focus, #settings-save-button:focus, #settings-cancel-button:focus {
	border-color:#4d90fe !important;
	}
	.r4 input[type="button"]:disabled, .r4 input[type="submit"]:disabled, .r4 button:disabled, #settings-save-button:disabled, #settings-cancel-button:disabled {
	   background-color:#fff !important;
	   border-color:#f3f3f3 !important;
	   color:#b8b8b8 !important
	}
}
