css:
/* by hkr (dreamflame in) */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url("about:addons"){
  *{color:rgba(200,225,255,.6);text-shadow:0 0 3px rgba(0,10,20,1)!important}
  page{
    background-image:linear-gradient(rgba(140,180,255,.2),rgba(0,12,20,0),rgba(0,12,30,.6)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;background-repeat:repeat,repeat!important;
    padding:15px 15px 15px 8px!important;
  }
  /* main panels ------------------------------------------------------------ \/ */
  box#view-port-container.main-content,richlistitem.category[selected]{
    background:linear-gradient(rgba(0,12,30,.6),rgba(0,12,30,.2))!important;
    box-shadow:inset 0 0 5px rgba(0,0,0,.5),0 0 0 1px rgba(255,255,255,.08)!important;
    border:none!important;
  }
  deck#view-port{box-shadow:inset 0 0 5px rgba(0,0,0,.5),0 0 5px 1px rgba(255,255,255,.06)!important}
  box#view-port-container.main-content{border-radius:8px!important;overflow:hidden!important}
  hbox.view-header.global-info-container,hbox#userstyle-sorting.view-header,hbox#userstyle-footer{
    background:linear-gradient(rgba(40,50,70,.4),rgba(0,12,20,.7))!important;
    border:none!important;
  }
  hbox.view-header.global-info-container,hbox#userstyle-sorting.view-header{
    box-shadow:inset 0 0 1px rgba(200,225,255,.2),inset 0 -1px 0 rgba(200,225,255,.04),inset 0 0 30px rgba(200,225,255,.12),0 1px 0 rgba(0,12,20,1),0 3px 0 rgba(0,12,20,.1),0 2px 0 rgba(0,12,20,.1)!important;
    border-radius:8px 8px 0 0!important;
  }
  hbox#userstyle-footer{
    box-shadow:inset 0 0 1px rgba(200,225,255,.2),inset 0 1px 0 rgba(200,225,255,.04),inset 0 0 30px rgba(200,225,255,.12),0 -1px 0 rgba(0,12,20,1),0 -2px 0 rgba(0,12,20,.4)!important;
    border-radius:0 0 8px 8px!important;
  }
  hbox.view-header.global-info-container,hbox#userstyle-sorting.view-header{padding:6px 8px 4px 8px!important}
  /* main panels ------------------------------------------------------------ /\ */
  richlistitem.category{max-width:58px!important}
  richlistitem.category *{margin-right:20px}
  button.sorter{
    background:linear-gradient(rgba(0,12,30,.6),rgba(0,12,30,.2)) no-repeat!important;
    box-shadow:0 0 1px rgba(200,225,255,.2),0 1px 0 rgba(200,225,255,.04),0 0 8px rgba(200,225,255,.1),inset 0 1px 0 rgba(0,12,20,1),inset 0 2px 0 rgba(0,12,20,.2),inset 0 3px 0 rgba(0,12,20,.2)!important;
    min-height:14px!important;
    height:14px!important;
    padding:2px 0 2px 8px!important;
    margin:3px 6px!important;
  }
  button.sorter,button.sorter *,button.sorter:focus,button.sorter:focus *{outline:none!important}
}
