css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

body, #branded-page-body-container, #body-container {background:#222222 url("http://r1.ilikewallpaper.net/iphone-panoramic-wallpapers/download/15354/Music-Equalizer-iphone-panoramic-wallpaper-ilikewallpaper_com_200.jpg") fixed no-repeat right top!important; background-size:100% 100%!important;}

.vm-separator, #footer, #vm-choosesub-newvideos, .sb-notif-off, .guide-section-separator, .start.end, div#footer-hh-container, span.separator, HR, .yt-uix-hovercard-card-body, #feed-pyv-container, #footer-container {display:none!important;}

#watch-like, #watch-dislikem #watch-dislike span, #watch-like span, .yt-uix-button.yt-uix-button-text.yt-uix-tooltip.yt-uix-button-empty, SPAN.yt-subscription-button-subscriber-count-unbranded-vertical.yt-uix-tooltip, SPAN.video-thumb.yt-thumb.yt-thumb-138 {background:transparent!important; border-radius:8px!important;}

DIV.watched-badge:before {content:"ALREADY "!important; color:red!important; visibility:visible!important; text-align:left!important;}

DIV.watched-badge {padding-top:30px!important; color:red!important; font-size:1.5em!important; left:-3px!important; opacity:1!important; text-shadow:-2px 0 10px black, 0px 12px 12px black, 4px 10px 10px #ff0000, 0 -22px 0px white!important;}

.yt-uix-shelfslider-prev:not(:hover) {background:white!important; opacity:0.9!important;}

.yt-uix-shelfslider-next:not(:hover) {background:white!important; opacity:0.9!important;}

.yt-uix-shelfslider-next {position:relative!important; left:690px!important;}

.yt-uix-shelfslider-prev {position:relative!important; left:0px!important;}

.branded-page-related-channels.yt-card.branded-page-gutter-padding {position:relative!important; left:200px!important;}


/* div.feed-author-bubble-container img {width:190px!important; height:190px!important;} */

}
@-moz-document url("http://www.youtube.com/"),
url("https://www.youtube.com/"),
url("http://www.youtube.com/feed/playlists"), url("https://www.youtube.com/feed/playlists"),
url-prefix("http://www.youtube.com/user/"), url-prefix("https://www.youtube.com/user/"),
url-prefix("http://www.youtube.com/playlist?"), url-prefix("https://www.youtube.com/playlist?"){
#content div div{background: transparent !important;border:none!important;box-shadow:none!important;}
}
