css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://rainbowdash.net/") {
@media (min-width: 800px) {
body {float: right; max-width: 100%;}
#site_nav_global_primary {width: 100% !important;}
#site_nav_global_primary ul {width: auto !important;}
#videosync iframe {padding: 0 !important;position:fixed!important;right:0!important;top:0!important;padding-bottom:1000px!important;width:600px!important;height:450px!important; background-position: 0 50px!important;}
#wrap {margin: 0 600px -999px 0 !important; width: auto !important; max-width: 976px !important;}
#core {width: 100% !important; -moz-box-sizing: border-box !important;}
#content {max-width: 100% !important; -moz-box-sizing: border-box !important; width: 686px !important;}
#videosync_aside {position:fixed!important;top:455px!important;left:auto!important;right:5px!important;z-index:9999!important;width:400px!important;max-height:250px!important;overflow:auto!important;}
}
@media (max-width: 1580px) AND (min-width: 800px) {
address {width: 100px !important;overflow: hidden!important;}
address img {float: left !important}
#site_nav_local_views {display:none!important;}
#core:before {max-width: 100% !important;}
#videosync_btn {left: 4px !important; top: 75px !important;}
}
}
