css:
/* by hkr (dreamflame in) */
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
window[stylish-url="chrome://mozapps/content/downloads/downloads.xul"] #navigator-toolbox #nav-bar{display:none!important}
@-moz-document url-prefix('chrome://'),url-prefix("about:"){
  label,description{color:rgba(160,190,255,1)!important}
  .hud-console-wrapper *{color:hsl(210,30%,64%)!important}
/* ------------------------------------ Various Button styles \/ */
  #appmenu-toolbar-button:hover,#appmenu-button:hover{opacity:1!important}
  spacer#titlebar-spacer,vbox#titlebar,#appmenu-toolbar-button>.toolbarbutton-text,#appmenu-button>.button-box.button-text,#appmenu-button>.button-box{display:none!important}
  #appmenu-toolbar-button,#appmenu-button{
    background:url("data:image/png;base64,") top right!important;
    width:32px!important;
    height:24px!important;
    box-shadow:0 0 5px rgba(0,12,20,1) inset!important;
    text-shadow:none!important;
    padding:0!important;
    margin:0!important;
    border:none!important;
    border-radius:0!important;
    opacity:.6!important;
    z-index:-9999!important;
  }
  button:not(.breadcrumbs-widget-item):not(.searchbar-engine-button):not(.sorter):not(.button-link),toolbox#stylishCustomToolbox toolbarbutton{
    -moz-appearance:none!important;
    background:linear-gradient(rgba(200,225,255,.18),rgba(200,225,255,.01) 50%,rgba(0,12,20,.1) 51%,rgba(0,12,20,0)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") repeat!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.06),inset 0 0 18px rgba(0,12,20,.6),0 0 0 1px rgba(0,12,20,.3),0 0 6px rgba(0,12,20,.4)!important;
    font-weight:900!important;
    color:rgba(0,0,5,.0)!important;
    text-shadow:-1px 0 rgba(1,0,5,.32),0 1px rgba(0,0,5,.32),1px 0 rgba(0,0,5,.32),0 -1px rgba(0,0,5,.32),2px 2px 3px rgba(200,225,255,.16),-2px -2px 4px rgba(0,12,26,.4)!important;
    border-radius:5px!important;
    border:0!important;
    padding:5px!important;
    transition:.4s ease-out,color .4s ease-out,background-image .4s ease-out,box-shadow .4s ease-out!important;
  }
  button:not(.breadcrumbs-widget-item):not(.searchbar-engine-button):not(.sorter):not(.button-link):hover,toolbox#stylishCustomToolbox toolbarbutton:hover{
    background:linear-gradient(rgba(200,225,255,.4),rgba(200,225,255,.08) 50%,rgba(200,225,255,.02) 51%,rgba(200,225,255,.02)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") repeat!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.08),inset 0 0 18px rgba(0,12,20,.8),0 0 0 1px rgba(0,12,20,.8),0 0 6px rgba(0,12,20,.24)!important;
    color:rgba(160,200,255,.5)!important;
  }
/* ------------------------------------ Various Button styles /\ */
/* ------------------------------------ Toolbar styles \/ */
  #titlebar-buttonbox-container,statuspanel#colorzilla-top-statusbar-display{
    display:none!important;
    -moz-appearance:none!important;
    margin-right:-20px!important;
  }
  /* window,*/ browser,dialog,wizard,prefwindow,.hud-console-wrapper,.hud-console-wrapper>*,
  deck:not(#tab-view-deck):not(#updateDeck):not(#buttonDeck):not(#setDefaultPane),
  toolbox#stylishCustomToolbox,toolbar,caption{background-image:linear-gradient(rgba(140,180,255,.2),rgba(0,12,20,0),rgba(0,10,30,.6)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;background-repeat:no-repeat,repeat!important}
  #TabsToolbar.toolbar-primary{padding:0 0 0 0!important}
  #TabsToolbar{background-image:linear-gradient(rgba(140,180,255,.2),rgba(0,12,20,0)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;background-repeat:no-repeat,repeat!important}
  .hud-box>.hud-outer-wrapper>vbox.hud-console-wrapper>hbox>*,.hud-console-filter-toolbar,richlistbox.hud-output-node,prefpane,tabbox,tabbox>tabs,tabpanels,tabpanel,caption,filefield>*,richlistbox[orient="vertical"],treecol,prefwindow#BrowserPreferences>*{background:transparent!important}
  prefwindow,prefpane,tabbox,tabs,tab,tabpanels,tabpanel,caption,filefield,richlistbox[orient="vertical"],richlistbox[orient="vertical"] richlistitem,listheader,treecol,listbox,prefwindow#BrowserPreferences>*{-moz-appearance:none!important}
  caption,tabbox>tabs>tab,treecol{padding:1px 3px!important}
  richlistbox[orient="vertical"] richlistitem{
    background:rgba(0,0,0,0)!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.01)!important;
    text-shadow:0 0 4px rgba(0,0,0,1)!important;
    border:none!important;
  }
  richlistbox[orient="vertical"] richlistitem[selected]{
    background:rgba(0,0,0,.2)!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.08)!important;
    border:none!important;
  }
  caption,richlistbox[orient="vertical"],listheader,listbox{
    background:rgba(0,0,0,.2)!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.08),0 0 0 1px rgba(30,40,60,1)!important;
    margin:0 2px 2px 2px!important;
    border-radius:5px 5px 0 0!important;
    text-shadow:0 0 4px rgba(0,0,0,1)!important;
  }
  tabbox>tabs>*:not(tab),listbox{border:none!important}
  listheader>treecol{border:1px solid rgba(0,0,0,.4)!important}
  prefwindow tabbox>tabs>tab{margin-bottom:-1px!important}
  prefwindow tabpanels,prefwindow tabbox>tabs>tab{
    box-shadow:inset 0 0 16px rgba(200,215,255,.1),inset 0 0 0 1px rgba(200,225,255,.1),0 0 2px rgba(0,0,0,1)!important;
    border:solid 1px black!important;
  }
  toolbox#navigator-toolbox{padding:0!important}
  toolbar#TabsToolbar{
    -moz-binding:url("chrome://global/content/bindings/toolbar.xml#toolbar")!important;
    margin-top:7px!important;
    margin-right:106px!important;
  }
  toolbar#toolbar-menubar,#navigator-toolbox::after{background:transparent!important}
  toolbar#nav-bar,#browser-bottombox,findbar{
    background:linear-gradient(rgba(140,180,255,.2),rgba(0,12,20,0)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;background-repeat:no-repeat,repeat!important;
    padding:1px 0!important;
  }
  #browser-bottombox,findbar{
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.1),0 0 4px rgba(0,12,20,1)!important;
    color:rgba(140,160,180,.8)!important;
  }
  #browser-bottombox{padding:0!important}
  toolbox#navigator-toolbox toolbarbutton,#browser-bottombox findbar#FindToolbar toolbarbutton,#browser-bottombox findbar#FindToolbar checkbox{
    color:rgba(140,160,180,.8)!important;
    -moz-appearance:none!important;
    border:0!important;
    opacity:.6;
    transition:opacity .4s ease-out!important;
  }
  toolbox#navigator-toolbox toolbarbutton:hover:not([disabled="true"]), #browser-bottombox findbar#FindToolbar toolbarbutton:hover:not([disabled="true"]), #browser-bottombox findbar#FindToolbar checkbox:hover:not([disabled="true"]) { opacity: 1; }
  .panel-arrow[side="top"] {
    list-style-image: url("data:image/png;base64,") !important;
    margin-bottom: -6px !important;
  }
  panel#notification-popup>box>box:last-of-type{
    background:linear-gradient(rgba(10,20,40,0),rgba(0,4,8,.5)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") 0 0 repeat!important;
    border:0!important;
    box-shadow:inset 0 0 60px 2px rgba(160,180,255,.16),inset 0 0 0 1px rgba(0,0,0,1),inset 0 0 0 2px rgba(160,180,255,.15)!important;
  }
  panel#notification-popup>box>box:last-of-type *{
    color:rgba(200,225,255,.8)!important;
    text-shadow:0 0 4px rgba(10,20,40,1)!important;
  }
  panel#notification-popup button>*:not(menupopup),panel#notification-popup button.popup-progress-cancel{
    background:transparent!important;
    border:0!important;
    box-shadow:none!important;
  }
  toolbarseparator{
    border:0!important;
    padding:3px!important;
  }
  toolbox#stylishCustomToolbox,toolbox#stylishCustomToolbox>toolbar{
    border:0 solid black!important;
    -moz-appearance:none!important;
  }
/* ------------------------------------ Toolbar styles /\ */
/* ------------------------------------ Tab styles \/ */
  tabs{box-shadow:0 0 5px rgba(0,12,20,1) inset!important}
  tab{
    background:linear-gradient(rgba(80,150,255,.3),rgba(10,30,50,.1)) no-repeat!important;
    color:rgba(160,200,255,.8)!important;
    text-shadow:0 0 8px rgba(0,0,0,1)!important;
    box-shadow:0 0 10px rgba(0,0,0,.5)!important;
    padding:0!important
  }
  tab:hover,menu[_moz-menuactive="true"],menuitem[_moz-menuactive="true"]{background:linear-gradient(rgba(80,155,255,.3),rgba(80,155,255,.1)) no-repeat!important}
  tab[selected="true"]{background:linear-gradient(rgba(10,20,30,.5),rgba(100,155,245,.4)) no-repeat!important}
  /*tab[pinned]{padding:1px 0 3px!important}/*removed as of 17.07.2014*/
  tab[pinned][titlechanged]:not([selected="true"]):not(:hover){
    background-image:
      radial-gradient(farthest-side at center bottom,hsla(72,96%,85%,1),hsla(200,100%,99%,0)),
      radial-gradient(farthest-side at center bottom,hsla(200,100%,70%,.8),hsla(200,100%,60%,0)),
      linear-gradient(rgba(80,155,255,.3),rgba(80,155,255,.1)),
      radial-gradient(circle farthest-corner at center 3px,rgba(186,221,251,.9),rgba(186,221,251,.6) 25%,rgba(186,221,251,0))
      !important;
    background-size:32px 1px,32px 5px,32px 28px,32px 28px!important;
    background-repeat:no-repeat,no-repeat,no-repeat,no-repeat!important;
    background-position:bottom center,bottom center,0 0,0 0!important;
    background-attachment:local,local,local,local!important;
  }
  tab[pinned][titlechanged]:not([selected="true"]):hover{
    background-image:radial-gradient(farthest-side at center bottom,hsla(220,100%,96%,1),hsla(200,100%,99%,.2)),radial-gradient(farthest-side at center bottom,hsla(200,100%,80%,1),hsla(200,90%,72%,0)),linear-gradient(rgba(80,155,255,.3),rgba(80,155,255,.1)),radial-gradient(circle farthest-corner at center 3px,rgba(186,221,251,1),rgba(186,221,251,1) 25%,rgba(186,221,251,0))!important;
    background-size:90% 1px,100% 8px,100% 100%,100% 100%!important;background-repeat:no-repeat,no-repeat,no-repeat,no-repeat!important;background-position:bottom center,bottom center,0,0!important;background-attachment:local,local,local,local!important;
  }
#main-window #navigator-toolbox #TabsToolbar .tabbrowser-tab[pinned][titlechanged]:not([selected="true"])>.tab-stack>.tab-content{background:transparent!important}
/* ------------------------------------ Tab styles /\ */
/* ------------------------------------ Menu styles \/ */
  toolbar#toolbar-menubar{background:linear-gradient(rgba(0,10,30,.3),rgba(90,160,255,.24)) no-repeat!important}
  toolbar#toolbar-menubar:not([autohide="true"]){padding-top:4px!important;margin-top:-4px!important}
  menubar,menu,menupopup,menuitem,menuseparator,menulist{-moz-appearance:none!important}
  menu:first-of-type:not(:only-of-type){border-radius:0 0 0 4px!important}
  menu:last-of-type:not(:only-of-type){border-radius:0 0 4px 0!important}
  menubar,menulist{
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.08),0 0 0 1px rgba(30,40,60,1)!important;
    margin:0 2px 2px 2px!important;
    border-radius:0 0 5px 5px!important;
  }
  menu,menulist{background:transparent!important;border:0!important}
  menuitem{
    background:linear-gradient(rgba(80,155,255,.1),transparent 100%) no-repeat!important;
    color:rgba(200,200,200,1)!important;
    text-shadow:0 0 3px rgba(0,0,0,1),0 0 8px rgba(0,0,0,1)!important;
    border-top:1px solid rgba(140,160,200,.22)!important;
    border-bottom:1px solid rgba(0,0,0,.4)!important;
    box-shadow:inset 0 0 10px rgba(200,235,255,.1)!important;
    padding:2px;
  }
  menu,menuitem{
    color:rgba(160,200,255,.8)!important;
    text-shadow:0 0 8px rgba(0,0,0,1)!important;
    box-shadow:0 0 10px rgba(0,0,0,.5)!important;
  }
  menu[_moz-menuactive="true"]:not([disabled="true"]),menuitem[_moz-menuactive="true"]:not([disabled="true"]){background:linear-gradient(rgba(80,155,255,.3),rgba(80,155,255,.1)) no-repeat!important}
  menupopup{
    background:linear-gradient(rgba(80,175,255,.3),transparent 50%,rgba(0,10,30,1)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") repeat!important;
    border:1px solid rgba(0,0,0,1)!important;
    border-radius:4px!important;
  }
  menupopup>hbox>vbox{background:transparent!important;box-shadow:none!important}
  menuseparator{
    background:linear-gradient(transparent,rgba(80,155,255,.1)) no-repeat!important;
    border:0!important;
    height:10px!important;
    padding:0!important;
  }
/* ------------------------------------ Menu styles /\ */
/* ------------------------------------ Identity box Styles \/ */
  box#identity-box:not(.verifiedDomain):not(.verifiedIdentity){background:transparent!important}
  box#identity-box.verifiedDomain{
    background:linear-gradient(rgba(80,155,255,.3),rgba(20,30,60,.6)) no-repeat!important;
    color:rgba(160,200,255,.8)!important;
    box-shadow:inset 0 1px 0 hsla(215,54%,33%,.05)!important;
    text-shadow:0 0 3px rgba(0,0,0,1)!important;
  }
  box#identity-box.verifiedDomain:hover{
    background:linear-gradient(rgba(80,155,255,.5),rgba(20,30,60,.8)) no-repeat!important;
    color:rgba(205,225,255,.8)!important;
  }
  box#identity-box.verifiedDomain[open="true"],#identity-box.verifiedDomain:hover:active{
    background:linear-gradient(rgba(20,30,60,.8),rgba(80,155,255,.3)) no-repeat!important;
    box-shadow:inset 0 1px 1px hsla(215,54%,33%,.7),inset 0 1px 3px 1px hsla(215,54%,33%,.5)!important;
  }
  box#identity-box.verifiedIdentity{
    background:linear-gradient(rgba(80,255,155,.3),rgba(20,60,30,.6)) no-repeat!important;
    color:rgba(160,255,200,.8)!important;
    box-shadow:inset 0 1px 0 hsla(165,54%,33%,.05)!important;
    text-shadow:0 0 3px rgba(0,0,0,1)!important;
  }
  box#identity-box.verifiedIdentity:hover{
    background:linear-gradient(rgba(80,255,155,.6),rgba(20,60,30,.9)) no-repeat!important;
    color:rgba(205,225,255,.8)!important;
  }
  box#identity-box.verifiedIdentity[open="true"],#identity-box.verifiedIdentity:hover:active{
    background:linear-gradient(rgba(20,60,30,.9),rgba(80,255,155,.4)) no-repeat!important;
    box-shadow:inset 0 1px 1px hsla(165,54%,33%,.7),inset 0 1px 3px 1px hsla(165,54%,33%,.5)!important;
  }
  box#notification-popup-box{
    background:url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") center right no-repeat transparent!important;
    padding-right:8px!important;
    padding-bottom:1px!important;
    margin-bottom:-1px!important;
    -moz-border-image:none!important;
    box-shadow:inset 1px 0 0 rgba(205,225,255,.06)!important;
    border-right:0!important;
  }
/* ------------------------------------ Identity box Styles /\ */
/* ------------------------------------ Progressbar Styles \/ */
  progressmeter .progress-bar{
    -moz-appearance:none!important;
    color:transparent!important;
    background:url("http://i.dreamflame.in/?i=ff/progresschunk.png&nl") right center no-repeat!important;
  }
  window progressmeter{box-shadow:inset 0 0 5px rgba(0,0,0,1),0 0 2px rgba(0,0,0,1),0 0 0 1px rgba(205,225,255,.1),0 0 10px rgba(205,225,255,.1)!important}
  progressmeter{
    -moz-appearance:none!important;
    background:url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl") 0 0 repeat!important;
    padding:0 1px!important;
    border:0!important;
    box-shadow:none!important;
    border-radius:4px!important;
    margin:4px!important;
  }
  /*fix progressbar cancel button width*/
  .download-progress .start-cap,
  .download-progress[complete] .end-cap,
  .download-progress[mode="undetermined"] .end-cap,
  .download-progress .progress .progress-bar{
    background:transparent!important;
    border:0 transparent!important;
  }
/* ------------------------------------ Progressbar Styles /\ */
/* ------------------------------------ Textbox and input styles \/ */
  textbox{
/*
    background:linear-gradient(rgba(200,225,255,.08),rgba(200,225,255,0)) no-repeat!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.08),0 0 0 1px rgba(0,12,20,.1),0 0 10px rgba(0,12,20,.2)!important;
    color:rgba(140,160,180,.8)!important;
    text-shadow:0 0 5px rgba(0,12,20,1)!important;
    padding-left:5px!important;
    padding-right:4px!important;
    border-width:0!important;
    -moz-appearance:none!important;
*/

    background:linear-gradient(rgba(200,225,255,.08),rgba(200,225,255,0)) no-repeat!important;
    box-shadow:0 0 1px rgba(200,225,255,.8) inset!important;
    min-height:22px;
    color:rgba(140,160,180,.8)!important;
    text-shadow:0 0 5px rgba(0,12,20,1)!important;
    padding:1px 3px;
    transition:opacity .4s ease-out,color .4s ease-out,background-image .4s ease-out,box-shadow .4s ease-out!important;
    border-radius:3px!important;
    border-width:0!important;
    -moz-appearance:none!important;
    -webkit-appearance:none!important;
  }
  textbox[focused]{
    background:linear-gradient(rgba(205,225,255,0),rgba(205,225,255,.08))!important;
    box-shadow:0 0 16px 0 rgba(0,12,20,.3) inset,0 0 0 2px rgba(0,12,20,.1) inset,0 0 0 1px rgba(205,225,255,.12) inset,0 0 16px rgba(205,225,255,.06)!important;
  }
  textbox#urlbar,searchbar#searchbar textbox{
    background:linear-gradient(rgba(200,225,255,.1),rgba(200,225,255,0))!important;
    box-shadow:0 0 0 1px rgba(205,225,255,.08) inset,0 0 0 1px rgba(0,12,20,.1),0 0 3px rgba(0,12,20,.7)!important;
    border:0!important;
    color:rgba(140,160,180,.8)!important;
    text-shadow:0 0 5px rgba(0,0,0,1)!important;
    padding:0 0 1px 0!important;
    border-radius:3px!important;
  }
/* 23.03.2014 New FF UI fixes */
  urlbar:not(:-moz-lwtheme),textbox#urlbar,textbox#urlbar:not(:-moz-lwtheme),
  window:not([chromehidden~="toolbar"]) #urlbar-container > #urlbar-wrapper > #urlbar:-moz-locale-dir(ltr){
    border:0!important;
    border-width:0!important;
    border-image-width:0!important;
    border-bottom-color:red!important;
    border-radius:3px!important;
  }
  textbox#urlbar[focused],searchbar#searchbar textbox[focused]{
    background:linear-gradient(rgba(200,225,255,0),rgba(200,225,255,.08))!important;
    box-shadow:inset 0 0 12px 0 rgba(0,12,20,.5),inset 0 0 0 1px rgba(0,12,20,.2),0 0 0 1px rgba(200,225,255,.12),0 0 16px rgba(205,225,255,.08)!important;
  }
  searchbar#searchbar textbox{
    transition:1s ease-out!important;
  }
  searchbar#searchbar textbox[focused]{min-width:280px!important}
/* ------------------------------------ Textbox and input styles /\ */
/* ------------------------------------ Groupbox styles \/ */
  groupbox{
    background:linear-gradient(rgba(140,180,255,.06),rgba(0,10,30,.08))!important;
    border:1px solid rgba(10,12,20,.5)!important;
    box-shadow:inset 0 0 0 1px rgba(200,0,0,1),10px 10px 2px 1px rgba(200,0,0,6)!important;
    border-radius:5px!important;
    -moz-appearance:none!important;
  }
/* ------------------------------------ Groupbox styles /\ */
/* ------------------------------------ Tooltip styles \/ */
  tooltip,statuspanel>hbox>*{
    -moz-appearance:none!important;
    padding:4px!important;
    border:0!important;
    background:linear-gradient(rgba(40,50,70,.8),rgba(0,12,20,1)) no-repeat!important;
    box-shadow:inset 0 0 0 1px rgba(200,225,255,.12),0 1px 10px rgba(0,12,20,1)!important;
    border-radius:3px!important;
  }
  
/* ------------------------------------ Tooltip styles /\ */
/* ------------------------------------ Force Black Icons on Firefox Toolbar Buttons \/ */
  #main-window:-moz-any(#back-button,#forward-button,#reload-button,#stop-button,#home-button,#print-button,#downloads-button,#history-button,#bookmarks-button,#bookmarks-menu-button,#new-window-button,#cut-button,#copy-button,#paste-button,#fullscreen-button,#zoom-out-button,#zoom-in-button,#sync-button:not([status="active"]),#feed-button,#new-tab-button)>.toolbarbutton-icon{list-style-image:url("chrome://browser/skin/Toolbar.png")!important}
  #main-window:-moz-any([stylish-application-version^="9"],[stylish-application-version^="10"],[stylish-application-version^="11"],[stylish-application-version^="12"]) #alltabs-button[type="menu"]{list-style-image:url("chrome://browser/skin/mainwindow-dropdown-arrow.png")!important}
  #main-window:-moz-any([stylish-application-version^="13"],[stylish-application-version^="14"],[stylish-application-version^="15"],[stylish-application-version^="16"]) #alltabs-button[type="menu"],
  #main-window:-moz-any([stylish-application-version^="13"],[stylish-application-version^="14"],[stylish-application-version^="15"],[stylish-application-version^="16"]) .toolbarbutton-1 .dropmarker-icon{list-style-image:url("chrome://browser/skin/toolbarbutton-dropdown-arrow.png")!important}
  #main-window #tabview-button{list-style-image:url("chrome://browser/skin/tabview/tabview.png")!important}
  #main-window .tabs-newtab-button,#main-window #TabsToolbar #new-tab-button>.toolbarbutton-icon{list-style-image:url("chrome://browser/skin/tabbrowser/newtab.png")!important}
  #main-window .scrollbutton-up,#main-window .scrollbutton-down{list-style-image:url("chrome://browser/skin/tabbrowser/tab-arrow-left.png")!important}
/* ------------------------------------ Force Black Icons on Firefox Toolbar Buttons /\ */
}
@-moz-document url-prefix("about:neterror"),url-prefix("about:certerror"){
  *{transition:.4s ease-out!important}
  html{
    background-image:linear-gradient(rgba(140,180,255,.2),rgba(0,12,20,0)),url("http://i.dreamflame.in/?i=fb_tile_v2.png&nl")!important;
    background-repeat:no-repeat,repeat!important;
    height:100%!important;
  }
  button{min-width:120px!important;height:36px!important}
  #errorTitleText,#errorShortDescText{
    border-bottom:1px solid rgba(0,12,20,1)!important;
    box-shadow:0 1px 0 0 rgba(200,225,255,.12)!important;
  }
  div#errorPageContainer{
    color:rgba(0,12,26,1)!important;
    text-shadow:2px 2px 3px rgba(200,225,255,.12)!important;
    background:rgba(0,0,0,.2)!important;
