/* fx about:newtab bg - [built-in] Firefox logo */
@-moz-document url("about:newtab")
/*b*/{
#newtab-scrollbox
  { background-image: url("chrome://branding/content/about-logo.png") ,  
      -moz-radial-gradient(center 90deg,ellipse contain,
          /* pick a color: */
          /*  magenta  *+/  #707,#606,#505,#404,#303,#202,#101,#000  /*.*/
          /*  green    *+/  #070,#060,#050,#040,#030,#020,#010,#000  /*.*/
          /*  red      */  #700,#600,#500,#400,#300,#200,#100,#000  /*.*/
          /*  blue     *+/  #00b,#00a,#009,#008,#007,#006,#005,#004,#003,#002,#001,#000  /*.*/
      )
      !important;
    background-attachment: fixed !important;
    background-position: center !important;
    background-repeat: no-repeat !important; }
/*e*/}
