@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(joystiq.com) {
  .omniture, #outerslice, .channel-logo, #topleader, .bottomleader, #grid, img[alt="the end"], #adTacFr, #thestrip, #rsslinks, #consolemenu, #branding h1 a img, #col1, #col3, .quigoads, .contentad, #footer, div.post div[align="center"] br, div.post div[style="text-align: center;"] br, div.post object + br, #googleresults, div.post > div > a:first-child + br, #dogear_promo, .searchform > .clearfix, a[href="http://www.joystiq.com/tag/holidaze-2008"], #content > .searchform, #relatedstories, #instructions {
    display: none !important;
  }
  #branding h1, #branding {
    background: transparent !important;
    border: none !important;
  }
  #branding h1 a {
    display: block !important;
    width: 305px !important;
    height: 84px !important;
    background-image: url(data:image/png;base64,);
  }
  #header {
    height: 101px !important;
    background: #FFFFFF url(http://www.blogsmithmedia.com/www.joystiq.com/media/topbar.gif) repeat-x scroll bottom left !important;
    border-color: #D3D3D3 !important;
    border-style: solid;
    border-width: 1px 1px 0px !important;
  }
  #branding {
    display: inline !important;
  }
  #menu {
    background: transparent !important;
    padding-top: 49px !important;
    width: 100% !important;
    border-bottom-width: 0px !important;
    margin-top: 0px !important;
    height: auto !important;
  }
  .searchform {
    position: relative !important;
    margin-left: 307px !important;
    margin-right: 77px !important;
    float: none !important;
    width: auto !important;
    height: auto !important;
  }
  .searchform > form {
    width: auto !important;
  }
  #queryname {
    float: none !important;
    width: 100% !important;
  }
  #searchsubmit {
    left: auto !important; 
    position: absolute !important; 
    top: 0px !important; 
    right: -75px !important;
  }
  #container {
    width: auto !important;
    margin-left: 5px !important;
    margin-right: 5px !important;
  }
  #incontainer_main {
    background: transparent !important;
  }
  #content {
    width: auto !important;
  }
  div.post div[style="text-align: center;"] img, div.post div[align="center"] img, div.post object, div.post > div > a img, div.post > div > center img {
    float: left !important;
    margin-right: 5px !important;
  }
  .cmt_text {
    width: 100% !important;
    float: none !important;
  }
}
