css:
@-moz-document domain("youtube.com") {
#page-container,#footer-container {
 display:none;
}
#guide {
 display: none; 
}
.view-count {
   float:right;
    display: block;
  }
}

@-moz-document url-prefix("https://www.youtube.com/results") {
#page-container {
 display:block !important;
}
#guide {
 display: none !important; 
}
#filter-dropdown {
 display: block !important; 
}
}

@-moz-document url-prefix("https://www.youtube.com/watch") {
.video-thumb {
display: none !important; 
}
#page-container {
display:block !important;
}
.badge {
display:none; 
}
.yt-uix-button-subscription-container{
display: none !important; 
}

.related-list-item:hover .video-thumb {
display: block !important; 
}

.related-list-item:hover .badge {
display:block; 
}
.related-list-item:hover .yt-uix-button-subscription-container{
display: none !important; 
}

.video-time{
display: block !important;
position: relative;
color: #00F;
left: 0;
height: 40px;
width: 40px;
	z-index: 999999999999 !important;
background: #FFF;
color: #000;
color: #000 !Important;
}

#watch-discussion {
display: none !Important; 
}

.ux-thumb-wrap .yt-uix-button.addto-button {
padding: 0;
width: 22px;
height: 22px;
	line-height: normal;
	-moz-border-radius: 2px;
	-webkit-border-radius: 2px;
	border-radius: 2px;
right: 0;
position: absolute;
top: 0;
}
}

@-moz-document url-prefix("https://www.youtube.com/user") {
#page-container, #footer-container {
 display:block !important;
}
.branded-page-related-channels {
   display:none; 
  }
.video-thumb {
display: block !important; 
}
}
