css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Fix forward button */

#forward-button {-moz-image-region: rect(2px, 424px, 33px, 397px) !important ; }
#forward-button:hover {-moz-image-region: rect(36px, 424px, 67px, 397px) !important ; }
#forward-button:hover:active {-moz-image-region: rect(104px, 424px, 135px, 397px) !important ; }
#forward-button[disabled="true"] {-moz-image-region: rect(70px, 424px, 101px, 397px) !important ; }

/* Fix back button */

#back-button {-moz-image-region: rect(2px, 397px, 33px, 360px) !important ; }
#back-button:hover {-moz-image-region: rect(36px, 397px, 67px, 360px) !important ; }
#back-button:hover:active {-moz-image-region: rect(104px, 397px, 135px, 360px) !important ; }
#back-button[disabled="true"] {-moz-image-region: rect(70px, 397px, 101px, 360px) !important ; }

/* Fix back-forward dropmarker */

#back-forward-dropmarker {-moz-image-region: rect(2px, 438px, 33px, 424px) !important ; }
#back-forward-dropmarker:hover {-moz-image-region: rect(36px, 438px, 67px, 424px) !important ; }
#back-forward-dropmarker:hover:active {-moz-image-region: rect(104px, 438px, 135px, 424px) !important ; }
#back-forward-dropmarker[disabled="true"] {-moz-image-region: rect(70px, 438px, 101px, 424px) !important ; }
