css:
@-moz-document url("http://www.allmystery.de/"), 
url("https://www.allmystery.de/"), 
url-prefix("http://www.allmystery.de/themen"), 
url-prefix("https://www.allmystery.de/themen"),
url-prefix("http://www.allmystery.de/ena/"), 
url-prefix("https://www.allmystery.de/ena/"),
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/artikel/"),
url-prefix("http://www.allmystery.de/stats/"),
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/chat/"), 
url-prefix("https://www.allmystery.de/chat/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/wiki/"),
url-prefix("http://www.allmystery.de/pn"), 
url-prefix("https://www.allmystery.de/pn"), 
url-prefix("http://www.allmystery.de/fcg"), 
url-prefix("https://www.allmystery.de/fcg"), 
url-prefix("http://www.allmystery.de/am"), 
url-prefix("https://www.allmystery.de/am") {
body{
    background-image:url("http://static.allmystery.de/upics/d71e7a_weihnachten-blau-baum-wallpapers_1508_12.jpg");
    background-size: 100% 100% !important;
    background-attachment:fixed !important;
    }

.header, .mcell2, .mcell_left, .mcell_inhalt,  .mcell_inhalt2, .mcell_inhalt3, .mcell_inhalt4,  .mcell {
    background: none !important;
    background-color: rgba(0,0,0,0.7) !important;
    border: 1px solid #1F2C37 !important;
    }

.tabnav a {
    background: none !important;
    background-color: rgba(0,0,0,0.7) !important;
    }
  
.post, .post:nth-child(even), .paginatorLast, .paginator, .MainOverview td.Category, .MainOverview td.LastPost, td, tr, .fm td, .fh td, .fd td, .header_info, .message,  .message_info, #intro, .footer hr, 
 .message_info, .status, 
.useravatar, .fd, .in, .fd2, .fh2, .titelg, .blogs-prevpost.even,
 .blogs-prevpost, .voteup, .start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last
 {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid #1F2C37 !important;
    }

center, div.categoryHeader, table {
    background-color: rgba(0,0,0,0.4) !important;
    border: 1px solid #1F2C37 !important;
    }
  
div.categoryHeader {
    background-color: rgba(0,0,0,0.7) !important;
    border: 1px solid #1F2C37 !important;
    }  
  
.sufu {
    border: 1px solid #1F2C37 !important;
    }
    
.Voting-voted .Voting-bar-filling, input {
    background-color: rgba(80,105,150,0.9) !important;
    border: 1px solid #1F2C37 !important;
    }
    
#se, .quick_item_header,#chat_messages   {
    background-color: #000000 !important;
    border: 1px solid #1F2C37 !important;
    } 
    
.header,
.mcell, .mcell2, .mcell_left, .mcell_inhalt, .mcell_inhalt2, .mcell_inhalt3, .mcell_inhalt4, 
.postbox1, .postbox2, .MainOverview td.Category, .MainOverview td.LastPost, td, tr, .fm td, .fh td, .fd td, .header_info, .message,
 .message_info, #intro, textarea, .footer hr, input, .quick_item_header, 
.quick_item_selected, #se, #moreMenu, img.smallCategoryImage, .status, 
.useravatar, .in, .fd, .fd2, .fh2, .titelg, a img, .blogs-prevpost.even,
 .blogs-prevpost, .voteup, .Voting-bar-filling, .Voting-bar,
 .Voting .ui-widget-content .ui-state-default, div.thread_vote_container, div.categories div  {
    border-radius: 4px 4px !important;
    }

.tabnav a, a.mod  {
    border-radius: 2px 2px !important;
    } 

a.mod {
    border: 1px solid #ffffff !important;
    }

.tabnav a:hover, a.tabnav_selected {
    background-color: rgba(80,105,150,0.8) !important;
    box-shadow: 0px 0px 5px #1F2C37 !important;
    } 

.finfo {
    border: solid 1px transparent !important;
    }

.fbeitrag {
    border: solid 1px transparent !important;
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }
}

@-moz-document url("https://www.allmystery.de/fcgi/?m=messages_log"), 
url-prefix("https://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("https://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/fcgi/?m=settings"), 
url-prefix("https://www.allmystery.de/static/impressum/"), 
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("http://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("http://www.allmystery.de/fcgi/?m=settings") 
 {
.main {
    background-color: rgba(0,0,0,0.8) !important;
    border: 1px solid #1F2C37 !important;
    }




}
