css:
@-moz-document url-prefix("http://qb-online.com/QB3/mypage/swf"), url-prefix("http://qb-online.com/mypage/swf") {
* {
	font-family:"メイリオ-AAR",sans-serif !important;
	line-height: 150%;
}

p.qbun, p.answerpat, p.answer, dl.ansmessview{
  font-size: x-large !important;
  color: #000000 !important;
}

p{
  color: #000000 !important;
}

b{
  color: #006699 !important;
}

div.qnum{
  font-size: large !important;
  color: #000000 !important;
}

h4{
  font-size: 25px;
background: desaturate(lime, 60%);
  color: green;
  text-shadow: 0px 15px 5px rgba(0,0,0,0.1),
               10px 20px 5px rgba(0,0,0,0.05),
               -10px 20px 5px rgba(0,0,0,0.05);
  text-align: center; 
}

input[name="answer"], input[name="nextq"]{
  font-size: 250% !important;
  color: #000000 !important;
}

a[href*="announce/info"], a[href*="mailto:"], a[href*="www.medicmedia.com"]{
    font-size: 100% !important;
   visibility: hidden !important;
}

a[href*="javascript:void(0);"]{
    color: black !important;
    -webkit-transition: 0.1s;
    -moz-transition: 0.1s;
    -o-transition: 0.1s;
    -ms-transition: 0.1s;
    transition: 0.1s;
}

a:hover {
    color: #4A7A2A !important;
}

a:hover{text-decoration: underline}

div#dk_green{visibility: hidden !important;}

div#bnr_side{visibility: hidden !important;}
}

@-moz-document url-prefix("http://qb-online.com/QB3/mypage/swf______disable") {

div#main_area{width: 900px !important;}

div#container{width: 1900px !important;}

div#content{width: 1600px !important;}

div#head_banner{width: 1900px !important;}

}
