css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://duckduckgo.com/lite") {
    html {
      color: rgb(141, 141, 141) !important;
      background: none repeat scroll 0 0 rgb(48, 48, 48) !important; 
      font-family: 'Helvetica Neue','Segoe UI', Arial, sans-serif;
    }
    a { 
      color: rgb(201, 201, 201) !important;
    }
    a:visited {
      color: rgb(141, 141, 141) !important;
    }
    .header {
      font-size: 32px;
      font-weight: bold;
      color: #dc5e47;
    }
    .extra {
      margin-top: 0px;
      margin-bottom: 0px;
      height: 1.3em;
    }
    #end-spacer {
      height: 10em;
    }
    body {
      padding-left: 20px;
      width: 90%;
    }
    span.no-results {
      font-size: 110%;
    }
    .query {
      height: 28px;
      border-color: #dc5e47;
      border-style: solid solid solid solid;
      border-width: 1px 1px 1px 1px;
      -moz-border-radius: 3px;
      border-radius: 3px;
      font-size: 20px;
      padding: 5px 6px;
      text-align: left;
      width: 55%; 
      max-width: 600px;
    }
    .link-text {
      color: #f37901 !important;
      font-size: 77.4%;
    }
    .result-link {
      font-size: 107.1%;
    }
    .result-snippet {
      font-size: 77.4%;
    }

    .result-sponsored:hover {
     background: rgb(60, 60, 60) !important;
    }

    .result-sponsored {
      background: rgb(60, 60, 60) !important;
      border: 0px solid #fef9eb !important;
    }

    .did-you-mean {
      color: #EE4444;
      font-size: 107.1%;
    }
    a {
      text-decoration: none;
      color: #1168CC;
    }
    a:hover {
      text-decoration: underline;
    }
    a:visited {
      color: #6830BB;
    }
    .timestamp {
      color:#555555;
      font-size: 77.4%;
    }
    .navbutton {
      background-color:transparent;
      border: 0px none;
      cursor:pointer;
      font-size:16px;
      font-weight:bold;
      text-decoration:underline;
      color: #10bdbb !important;
      border-bottom: 1px solid transparent;
      padding: 0px;
    }
    input.query {
        color: rgb(201, 201, 201) !important;
        background-color: rgb(63, 63, 63) !important;
        border: 1px solid rgb(36, 36, 36) !important;
        box-shadow: 0px 0px 3px 1px rgb(102, 102, 102) inset !important;
    }
    input.navbutton:hover {
    	border-bottom: 1px solid #10bdbb !important;
    }
    @media only screen and (max-device-width: 700px) {
      body {
        width: 95%;
        margin-left: 10px;
        padding: 0;
      }
      .query {
        width: 180px;
        font-size: 12px;
      }
      .submit {
        font-size: 12px;
      }
    }
    @media only screen and (max-device-width: 701px) and (orientation:landscape) 
    {
      .query {
        width: 273px;
      }
    }
}
