css:
@-moz-document domain("etf2l.org") {
#wrapper {
font-family: Arial !important;
}

#Nvt {
display: none;
}

#Nav {
background-image: none !important;  
}  

#header .logo a {
margin: 0;
width: 290px;
height: 120px;
}

h2 {
border: 0;
}

ul li ul.children {
top: 30px;
}

ul#Nav li a {
line-height: 30px;
background: none;
}

ul#Nav li a:hover {
background-image: none;
}

#header {    
top: 0;
background-position: 0% 44%;  
height: 110px;
}


#sidebarHeader {
border: 0;
padding: 0;
height: 105px;
margin: 0;
padding-right: 10px;
padding-top: 5px;
width: 695px !important;
background: #3E5C76;
}

#sidebarHeader ul li {

}
#sidebarHeader ul li ul li {
display: inline;
padding-left: 15px;
font-size: 14px;
}

input#search_input {
background: #F9F9F9;
}
#sidebarHeader h2 {
border: 0;
}


.sidebar ul, .sidebar ol, .widget_table tbody td {
font-size: 12px;
}


#rs-discuss .post-wrapper {
border: 1px solid #CCCCCC;
}

#rs-discuss .rowgroup {
background: none;
}
#rs-discuss .category {
background: #f2f2f2 !important;
}
#rs-discuss .table-list {
background: #F9F9F9;
}
#rs-discuss .table-list td {
border: 1px solid #CCC;
}
#rs-discuss th {
background: #eee;
border: 1px solid #CCC;
color: #000;
}
#rs-discuss blockquote {
background: #F2f2f2;
border: 1px solid #CCC;
font-size: 12px;
font-family: inherit;
}

#rs-discuss .cell-name, #rs-discuss .cell-ractivity {
font-size: 12px;
padding-left: 5px;
}
.fix {
background: #F9F9F9;
font-size: 13px;
}

.fix a {
color: #002bb8;
}

.fix td {
border: 1px solid #AAAAAA;
}

.fix tr td, .fix tr th {
background: none;
}

.fix tr.comp td, .thistory {
background: #f2f2f2 !important;
}

div.post-block {
background: #F5F5F5;
}

div.post-block, div.post-block-odd {
font-size: 12px !important;
}

div.post-block-odd {
background: #F9F9F9 !important;
}

div.sidebar {
background: #F9F9F9;
border: 1px solid #CCCCCC;
margin-right: 10px;
margin-top: 20px;
width: 146px !important;
}

p.postmeta, .rec_search, #rs-discuss .breadcrumbs, .rec {
background: #F9F9F9 !important;
border: 1px solid #CCCCCC;
}

.bronze {
background: #BB8644;
}

.dropped {
color: #666;
}

tr.fix_title td, tr.fix_title th {
background: #f2f2f2 !important;
color: #000000 !important;
}

.gold {
background: #ffdf61;
}

.green {
background: rgb(208, 240, 192) !important;
}

.red {
background: #fcc;
}

.silver {
background: #cbcbcb;
}

#wrapper {
background: #FFFFFF !important;
font-family: sans-serif;
font-size: 13px;
}

.commentlist li, #commentform input, #commentform textarea {
font-size: 12px;
}

h4 {
font-size: 11px;
}
#footer {
background: #f2f2f2;
border-top: 1px solid #CCC;
color: #000;
}

.cup {
font-size: 13px
}

#sidebarSponsor img {
width: 140px !important;
height: auto;
}
}
